import React from 'react';

function VideosForm({ videoData, onSubmit, isSaving, onChange, editingVideo }) {
  // Handle form submission (prevents default form submission behavior)
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the default form submit behavior
    onSubmit(); // Call the passed onSubmit function (add or update)
  };

  return (
    <div className="Video-Search-Box">
      <form onSubmit={handleSubmit}> {/* Wrap the form inputs in a form */}
        <input
          type="text"
          name="url"
          placeholder="Video URL"
          value={videoData.url} // Bind to videoData (either new or editing)
          onChange={onChange} // Handle input changes
          className="video-search-input"
        />
        <textarea
          name="description"
          placeholder="Video Description"
          value={videoData.description} // Bind to videoData
          onChange={onChange} // Handle input changes
          className="video-search-input"
        ></textarea>
        <div className="video-search-buttons">
          <button
            type="submit"  // Ensure the button submits the form
            className="search-btn"
            disabled={isSaving} // Disable button when saving
          >
            {isSaving ? 'Saving...' : editingVideo ? 'Update Video' : 'Add Video'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default VideosForm;
