import React, { useState, useEffect, useContext, useCallback, useRef } from 'react';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import './Photos.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Photos() {
  const { auth } = useContext(AuthContext);
  const [photos, setPhotos] = useState([]);
  const [selectedphoto, setSelectedPhoto] = useState({});
  const urlRef = useRef();
  const descriptionRef = useRef();
  const { movieId } = useParams();

  // Fetch Photos Data from TMDB API
  const handleImportPhotos = async () => {
    try {
      const response = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}/images`, {
        headers: {
          Authorization: `eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1MGY0ZjFlMmNhODQ1ZjA3NWY5MmI5ZDRlMGY3ZTEwYiIsIm5iZiI6MTcyOTkyNjY3NC40NzIwOTksInN1YiI6IjY3MTM3ODRmNjUwMjQ4YjlkYjYxZTgxMiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.RRJNLOg8pmgYoomiCWKtwkw74T3ZtAs7ZScqxo1bzWg`, 
        },
      });
      const tmdbPhotos = response.data.backdrops;
      setPhotos(tmdbPhotos);
      alert('Photos imported successfully!');
    } catch (error) {
      console.error("Error importing photos:", error);
      alert('Failed to import photos.');
    }
  };

  // Get Photos Data (from your backend or other sources)
  useEffect(() => {
    const getAllPhotos = async () => {
      try {
        const response = await axios.get(`/movies/${movieId}/photos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setPhotos(response.data.photos);
      } catch (error) {
        console.error("Error fetching photos:", error);
      }
    };
    getAllPhotos();
  }, [movieId, auth.accessToken]);

  // Handle Save Photo
  const handlesave = async () => {
    const validateFields = () => {
      if (!urlRef.current.value.trim() || !descriptionRef.current.value.trim()) {
        alert('Both fields are required!');
        return false;
      }
      return true;
    };

    if (!validateFields()) {
      return;
    }

    const data = {
      userId: auth.user.userId,
      movieId,
      url: urlRef.current.value,
      description: descriptionRef.current.value,
    };

    try {
      await axios.post('/admin/photos', data, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });
      alert('Photo added successfully');
      setSelectedPhoto({});
      setPhotos([...photos, data]);
    } catch (error) {
      console.error('Error saving photo:', error);
    }
  };

  // Handle Delete Photo
  const handledelete = (id) => {
    const isConfirm = window.confirm('Are you sure you want to delete this photo?');
    if (isConfirm) {
      axios.delete(`/photos/${id}`, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      }).then(() => {
        alert('Photo deleted successfully');
        setPhotos(photos.filter((photo) => photo.id !== id));
      }).catch((err) => {
        console.error('Error deleting photo:', err);
      });
    }
  };

  return (
    <div className='photo-box'>
      <button onClick={handleImportPhotos}>Import Photos from TMDB</button>

      <div className='card-display-photo'>
        {photos.length > 0 ? (
          photos.map((image) => (
            <div key={image.file_path} className='card-photo'>
              <img
                src={`https://image.tmdb.org/t/p/w500/${image.file_path}`}
                alt={image.description}
                className='image-style'
                style={{ width: '100%' }}
              />
              <div className='container-photo'>
                <p>{image.description}</p>
                <button
                  type='button'
                  className='delete-button'
                  onClick={() => handledelete(image.file_path)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className='no-photo'>
            <h3>Photos not found</h3>
          </div>
        )}
      </div>

      <div className="photo-form">
        <input
          type="text"
          placeholder="URL"
          ref={urlRef}
          value={selectedphoto.url || ''}
          onChange={(e) => setSelectedPhoto({ ...selectedphoto, url: e.target.value })}
        />
        <textarea
          placeholder="Description"
          ref={descriptionRef}
          value={selectedphoto.description || ''}
          onChange={(e) => setSelectedPhoto({ ...selectedphoto, description: e.target.value })}
        />
        <button onClick={handlesave}>Save</button>
      </div>
    </div>
  );
}

export default Photos;
