import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Photo.css';

function Photos() {
  const { auth } = useContext(AuthContext);
  const { movieId } = useParams();
  const [photos, setPhotos] = useState([]);
  const [newPhoto, setNewPhoto] = useState({ url: '', description: '' });
  const [editingPhoto, setEditingPhoto] = useState(null);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get(`/movies/${movieId}/photos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setPhotos(response.data.photos || []);
      } catch (error) {
        console.error("Error fetching photos:", error);
      }
    };
    fetchPhotos();
  }, [movieId, auth.accessToken]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingPhoto) {
      setEditingPhoto((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewPhoto((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleAddPhoto = async () => {
    if (!newPhoto.url || !newPhoto.description) {
      alert("Please provide both a URL and a description for the photo.");
      return;
    }

    setIsSaving(true);
    try {
      const response = await axios.post('/photos', {
        ...newPhoto,
        movieId,
        userId: auth.user.userId,
      }, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });

      setPhotos((prevPhotos) => [...prevPhotos, response.data]);
      setNewPhoto({ url: '', description: '' });
      alert("Photo added successfully!");
    } catch (error) {
      console.error("Error adding photo:", error);
      alert("Failed to add photo.");
    } finally {
      setIsSaving(false);
    }
  };

  const handleDeletePhoto = async (id) => {
    if (window.confirm("Are you sure you want to delete this photo?")) {
      try {
        await axios.delete(`/photos/${id}`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setPhotos((prevPhotos) => prevPhotos.filter((photo) => photo.id !== id));
        alert("Photo deleted successfully!");
    } catch (error) {
      console.error("Error deleting photo:", error);
      alert("Failed to delete photo.");
    }
  }
};

const handleUpdatePhoto = async () => {
  if (!editingPhoto.url || !editingPhoto.description) {
    alert("Please provide both a valid URL and a description.");
    return;
  }

  const isConfirm = window.confirm("Are you sure you want to update the photo?");
  if (isConfirm) {
    setIsSaving(true);
    try {
      const updatedData = {
        url: editingPhoto.url,
        description: editingPhoto.description,
        movieId: editingPhoto.movieId,
      };

      const response = await axios.patch(`/photos/${editingPhoto.id}`, updatedData, {
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });

      setPhotos((prevPhotos) =>
        prevPhotos.map((photo) =>
          photo.id === editingPhoto.id ? { ...photo, ...updatedData } : photo
        )
      );

      alert("Photo updated successfully!");
      setEditingPhoto(null); // Clear editing state
    } catch (error) {
      console.error("Error updating photo:", error);
      alert("Failed to update photo.");
    } finally {
      setIsSaving(false);
    }
  }
};

return (
  <div className="photo-box">
    <div className="Photo-View-Box">
      <h2>Photos for Movie</h2>
      <div className="card-display-photo">
        {photos.length === 0 ? (
          <p>No photos found for this movie.</p>
        ) : (
          photos.map((photo) => (
            <div key={photo.id} className="card-photo">
              <img src={photo.url} alt={photo.description} className="image-style" />
              <div className="container-photo">
                <h4>{photo.description}</h4>
                <div className="buttons-group">
                  <button className="edit-button" onClick={() => setEditingPhoto(photo)}>
                    Edit
                  </button>
                  <button className="delete-button" onClick={() => handleDeletePhoto(photo.id)}>
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>

    <div className="Photo-Form">
      <h3>{editingPhoto ? 'Edit Photo' : 'Add New Photo'}</h3>
      <div className="input-group">
        <label>URL:</label>
        <input
          type="text"
          name="url"
          value={editingPhoto ? editingPhoto.url : newPhoto.url}
          onChange={handleInputChange}
        />
      </div>
      <div className="input-group">
        <label>Description:</label>
        <textarea
          name="description"
          value={editingPhoto ? editingPhoto.description : newPhoto.description}
          onChange={handleInputChange}
        />
      </div>
      <div className="buttons-group">
        {editingPhoto ? (
          <button onClick={handleUpdatePhoto} disabled={isSaving}>
            Update Photo
          </button>
        ) : (
          <button onClick={handleAddPhoto} disabled={isSaving}>
            Add Photo
          </button>
        )}
        <button onClick={() => {
          setEditingPhoto(null); // Clear editing state
          setNewPhoto({ url: '', description: '' }); // Clear new photo state
        }}>
          Clear
        </button>
      </div>
    </div>
  </div>
);
}

export default Photos;
