import React, { useState, useEffect } from 'react';

const PhotosForm = ({ state, setState, data, onSubmit, onChange, isSaving }) => {
  const [formData, setFormData] = useState({
    url: '',
    description: ''
  });

  // If `data` is passed (for update mode), initialize the form with existing data
  useEffect(() => {
    if (data) {
      setFormData({
        url: data.url || '',
        description: data.description || ''
      });
    }
  }, [data]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCancel = () => {
    setState('base');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="photo-form-container">
      <h3>{state === 'update' ? 'Update Photo' : 'Add New Photo'}</h3>
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="url">Photo URL</label>
          <input
            type="url"
            id="url"
            name="url"
            value={formData.url}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <input
            type="text"
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-buttons">
          <button type="submit" disabled={isSaving}>
            {isSaving ? 'Saving...' : state === 'update' ? 'Update Photo' : 'Add Photo'}
          </button>
          <button type="button" onClick={handleCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default PhotosForm;
