import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cast.css';

const Cast = ({ movieId }) => {
  const [casts, setCasts] = useState([]);
  const [castForm, setCastForm] = useState({
    name: '',
    characterName: '',
    url: '',
  });
  const [isEditingCast, setIsEditingCast] = useState(false);
  const [editingCastId, setEditingCastId] = useState(null);
  const [error, setError] = useState(null);
  const accessToken = localStorage.getItem('accessToken');
  const CAST_API_BASE_URL = 'http://localhost/movieproject-api/admin/casts';

  const fetchCasts = async () => {
    try {
      const response = await axios.get(`${CAST_API_BASE_URL}/${movieId}`, {
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      setCasts(response.data);
    } catch (err) {
      console.error('Failed to fetch casts:', err);
      setError('Failed to fetch casts. Please try again later.');
    }
  };

  const handleCastInputChange = (e) => {
    const { name, value } = e.target;
    setCastForm({ ...castForm, [name]: value });
  };

  const handleCastSubmit = async (e) => {
    e.preventDefault();
    if (!castForm.name || !castForm.characterName || !castForm.url) {
      alert('Please fill out all fields.');
      return;
    }

    try {
      if (isEditingCast) {
        await axios.patch(
          `${CAST_API_BASE_URL}/${editingCastId}`,
          { ...castForm, movieId },
          {
            headers: { Authorization: `Bearer ${accessToken}` },
          }
        );
        alert('Cast updated successfully.');
      } else {
        await axios.post(
          CAST_API_BASE_URL,
          { ...castForm, movieId, userId: 1 },
          {
            headers: { Authorization: `Bearer ${accessToken}` },
          }
        );
        alert('Cast added successfully.');
      }

      setCastForm({ name: '', characterName: '', url: '' });
      setIsEditingCast(false);
      fetchCasts();
    } catch (err) {
      console.error('Failed to save cast:', err);
      setError('Failed to save cast. Please try again later.');
    }
  };

  const handleEditCast = (cast) => {
    setCastForm({
      name: cast.name,
      characterName: cast.characterName,
      url: cast.url,
    });
    setIsEditingCast(true);
    setEditingCastId(cast.castId);
  };

  const handleDeleteCast = async (castId) => {
    if (window.confirm('Are you sure you want to delete this cast member?')) {
      try {
        await axios.delete(`${CAST_API_BASE_URL}/${castId}`, {
          headers: { Authorization: `Bearer ${accessToken}` },
        });
        alert('Cast deleted successfully.');
        fetchCasts();
      } catch (err) {
        console.error('Failed to delete cast:', err);
        setError('Failed to delete cast. Please try again later.');
      }
    }
  };

  useEffect(() => {
    if (movieId) {
      fetchCasts();
    }
  }, [movieId]);

  return (
    <div className="cast-management">
      <h2>Cast Management</h2>
      {error && <p className="text-danger">{error}</p>}

      <form onSubmit={handleCastSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={castForm.name}
            onChange={handleCastInputChange}
            required
          />
        </div>
        <div>
          <label>Character Name</label>
          <input
            type="text"
            name="characterName"
            value={castForm.characterName}
            onChange={handleCastInputChange}
            required
          />
        </div>
        <div>
          <label>Image URL</label>
          <input
            type="url"
            name="url"
            value={castForm.url}
            onChange={handleCastInputChange}
            required
          />
        </div>
        <button type="submit">{isEditingCast ? 'Update Cast' : 'Add Cast'}</button>
      </form>

      <hr />

      <h3>Current Cast Members</h3>
      {casts.length === 0 ? (
        <p>No cast members found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Character</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {casts.map((cast) => (
              <tr key={cast.castId}>
                <td>
                  <img src={cast.url} alt={cast.name} style={{ width: '50px' }} />
                </td>
                <td>{cast.name}</td>
                <td>{cast.characterName}</td>
                <td>
                  <button type="button" onClick={() => handleEditCast(cast)}>
                    Edit
                  </button>
                  <button type="button" onClick={() => handleDeleteCast(cast.castId)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Cast;
