import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Photo.css';

function Photos() {
  const { auth } = useContext(AuthContext);
  const { movieId } = useParams(); // Extract movieId from URL
  const [photos, setPhotos] = useState([]); // Define state for photos
  const [selectedPhoto, setSelectedPhoto] = useState({
    url: '', // Default empty string for URL
    description: '', // Default empty string for description
  });

  // Fetch all photos when the component mounts
  useEffect(() => {
    if (movieId) {
      axios
        .get(`/movies/${movieId}/photos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        })
        .then((response) => setPhotos(response.data)) // Set photos in state
        .catch((error) => console.error('Error fetching photos:', error));
    }
  }, [movieId, auth.accessToken]);

  // Validate input fields
  const validateField = (fieldRef, fieldName) => {
    if (!fieldRef.current.value.trim()) {
      fieldRef.current.style.border = '2px solid red';
      setTimeout(() => {
        fieldRef.current.style.border = '1px solid #ccc';
      }, 2000);
      console.log(`${fieldName} cannot be empty.`);
      return false;
    }
    return true;
  };

  // Save new photo
  const handleSave = () => {
    if (!selectedPhoto.url || !selectedPhoto.description) {
      alert('URL and Description are required.');
      return; // Prevent saving if URL or description is missing
    }

    const dataphoto = {
      userId: auth.user.userId,
      movieId: movieId,
      url: selectedPhoto.url,
      description: selectedPhoto.description,
    };

    axios
      .post('/photos', dataphoto, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      })
      .then(() => {
        alert('Photo added successfully!');
        setSelectedPhoto({ url: '', description: '' }); // Reset form after save
        // Refresh photo list
        axios
          .get(`/movies/${movieId}/photos`, {
            headers: { Authorization: `Bearer ${auth.accessToken}` },
          })
          .then((response) => setPhotos(response.data));
      })
      .catch((error) => {
        console.error('Error saving photo:', error);
      });
  };

  // Delete photo
  const handleDelete = (id) => {
    const isConfirmed = window.confirm('Are you sure you want to delete this photo?');
    if (isConfirmed) {
      axios
        .delete(`/photos/${id}`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        })
        .then(() => {
          alert('Photo deleted successfully');
          // Refresh photo list
          axios
            .get(`/movies/${movieId}/photos`, {
              headers: { Authorization: `Bearer ${auth.accessToken}` },
            })
            .then((response) => setPhotos(response.data));
        })
        .catch((error) => {
          console.error('Error deleting photo:', error);
        });
    }
  };

  // Update selected photo
  const handleUpdate = (id) => {
    if (!selectedPhoto.url || !selectedPhoto.description) {
      alert('URL and Description are required.');
      return;
    }

    const dataphoto = {
      userId: auth.user.userId,
      movieId: movieId,
      url: selectedPhoto.url,
      description: selectedPhoto.description,
    };

    axios
      .patch(`/photos/${id}`, dataphoto, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      })
      .then(() => {
        alert('Photo updated successfully!');
        setSelectedPhoto({ url: '', description: '' }); // Reset form
        // Refresh photo list
        axios
          .get(`/movies/${movieId}/photos`, {
            headers: { Authorization: `Bearer ${auth.accessToken}` },
          })
          .then((response) => setPhotos(response.data));
      })
      .catch((error) => {
        console.error('Error updating photo:', error);
      });
  };

  // Clear selected photo form
  const handleClear = () => {
    setSelectedPhoto({ url: '', description: '' });
  };

  return (
    <div className="photo-box">
      <div className="Photo-View-Box">
        {/* Display list of photos */}
        {photos && photos.length > 0 ? (
          <div className="card-display-photo">
            {photos.map((image) => (
              <div key={image.id} className="card-photo">
                <div className="buttons-group">
                  <button
                    type="button"
                    className="delete-button"
                    onClick={() => handleDelete(image.id)}
                  >
                    Delete
                  </button>
                  <button
                    type="button"
                    className="edit-button"
                    onClick={() => setSelectedPhoto(image)} // Set selected photo for editing
                  >
                    Edit
                  </button>
                </div>
                <img
                  src={image.url}
                  alt={image.description}
                  style={{ width: '100%' }}
                  className="image-style"
                />
                <div className="container-photo">
                  <p>{image.description}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-photo">
            <h3>No Photos Found</h3>
          </div>
        )}
      </div>

      {/* Photo form for adding/editing */}
      <div className="Photo-Search-Box">
        <div className="parent-container">
          <div className="photo-detail-box">
            <div className="photo-container-center">
              <div className="photo-image-container">
                <img
                  alt="photo-movies"
                  src={selectedPhoto.url || 'https://upload.wikimedia.org/wikipedia/commons/1/14/No_Image_Available.jpg'}
                  className="photo-img"
                />
              </div>
            </div>
          </div>
          <div className="photo-info-text">
            <div className="input-group">
              <label className="label-photo">Image URL:</label>
              <input
                className="photo-url"
                type="text"
                value={selectedPhoto.url || ''}
                onChange={(e) => setSelectedPhoto({ ...selectedPhoto, url: e.target.value })}
                placeholder="Enter image URL"
              />
            </div>
            <div className="input-group">
              <label className="label-photo">Description:</label>
              <textarea
                className="photo-description"
                value={selectedPhoto.description || ''}
                onChange={(e) => setSelectedPhoto({ ...selectedPhoto, description: e.target.value })}
                placeholder="Enter image description"
              />
            </div>
          </div>

          {/* Action buttons */}
          <div className="save-edit-back-btn">
            {!selectedPhoto.id ? (
              <button className="edit-save-btn" type="button" onClick={handleSave}>
                Save
              </button>
            ) : (
              <button className="edit-save-btn" type="button" onClick={() => handleUpdate(selectedPhoto.id)}>
                Update
              </button>
            )}
            <button className="clear-btn" type="button" onClick={handleClear}>
              Clear
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Photos;
