import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cast.css';

const Cast = ({ movieId }) => {
  const [tmdbCastSearch, setTmdbCastSearch] = useState(''); // Input for TMDB cast search
  const [tmdbResults, setTmdbResults] = useState([]); // Results from TMDB
  const [localCasts, setLocalCasts] = useState([]); // Casts saved in your database
  const [error, setError] = useState(null);
  const accessToken = localStorage.getItem('accessToken'); // Retrieve your app's access token

  // TMDB API details
  const TMDB_BASE_URL = 'https://api.themoviedb.org/3';
  const TMDB_AUTHORIZATION_TOKEN =
    'Bearer 6501881fa7adae43af40af405c1c89b4';
  const BACKEND_CASTS_API = 'http://localhost/movieproject-api/admin/casts'; // Your backend API endpoint

  // Fetch local casts from the backend
  const fetchLocalCasts = async () => {
    try {
      const response = await axios.get(`${BACKEND_CASTS_API}/${movieId}`, {
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      setLocalCasts(response.data);
    } catch (err) {
      console.error('Failed to fetch local casts:', err);
      setError('Failed to fetch local casts. Please try again later.');
    }
  };

  // Search cast from TMDB
  const searchTmdbCast = async () => {
    if (!tmdbCastSearch.trim()) {
      alert('Please enter a cast name to search.');
      return;
    }
    try {
      const response = await axios.get(
        `${TMDB_BASE_URL}/search/person?query=${tmdbCastSearch}&include_adult=false&language=en-US&page=1`,
        {
          headers: {
            accept: 'application/json',
            Authorization: TMDB_AUTHORIZATION_TOKEN,
          },
        }
      );
      setTmdbResults(response.data.results);
    } catch (err) {
      console.error('Failed to fetch TMDB cast:', err);
      setError('Failed to fetch TMDB cast. Please try again later.');
    }
  };

  // Add a cast to the local database
  const addCast = async (cast) => {
    const newCast = {
      movieId,
      userId: 1, // Replace with the logged-in user's ID if applicable
      name: cast.name,
      url: `https://image.tmdb.org/t/p/original${cast.profile_path}`,
      characterName: cast.known_for_department || 'Unknown',
    };

    try {
      await axios.post(BACKEND_CASTS_API, newCast, {
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      alert('Cast added successfully.');
      fetchLocalCasts(); // Refresh local casts
    } catch (err) {
      console.error('Failed to add cast:', err);
      setError('Failed to add cast. Please try again later.');
    }
  };

  // Delete a local cast
  const deleteCast = async (castId) => {
    if (window.confirm('Are you sure you want to delete this cast member?')) {
      try {
        await axios.delete(`${BACKEND_CASTS_API}/${castId}`, {
          headers: { Authorization: `Bearer ${accessToken}` },
        });
        alert('Cast deleted successfully.');
        fetchLocalCasts();
      } catch (err) {
        console.error('Failed to delete cast:', err);
        setError('Failed to delete cast. Please try again later.');
      }
    }
  };

  useEffect(() => {
    fetchLocalCasts(); // Fetch local casts when component mounts
  }, [movieId]);

  return (
    <div className="cast-management">
      <h2>Cast Management</h2>
      {error && <p className="text-danger">{error}</p>}

      {/* TMDB Search Section */}
      <div className="tmdb-search">
        <label>Search Cast from TMDB:</label>
        <input
          type="text"
          value={tmdbCastSearch}
          onChange={(e) => setTmdbCastSearch(e.target.value)}
          placeholder="Enter cast name"
        />
        <button onClick={searchTmdbCast}>Search</button>
      </div>

      {/* TMDB Results */}
      {tmdbResults.length > 0 && (
        <div className="tmdb-results">
          <h3>TMDB Results</h3>
          <ul>
            {tmdbResults.map((cast) => (
              <li key={cast.id}>
                <img
                  src={`https://image.tmdb.org/t/p/w200${cast.profile_path}`}
                  alt={cast.name}
                />
                <p>{cast.name}</p>
                <button onClick={() => addCast(cast)}>Add to Local</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      <hr />

      {/* Local Casts Section */}
      <h3>Saved Cast Members</h3>
      {localCasts.length === 0 ? (
        <p>No cast members found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Character</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {localCasts.map((cast) => (
              <tr key={cast.id}>
                <td>
                  <img src={cast.url} alt={cast.name} style={{ width: '50px' }} />
                </td>
                <td>{cast.name}</td>
                <td>{cast.characterName}</td>
                <td>
                  <button onClick={() => deleteCast(cast.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Cast;
