import React, { useEffect, useState, useContext, useCallback, useRef } from 'react';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import './Photo.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Photos() {
  const { auth } = useContext(AuthContext);
  const [photoId, setPhotoId] = useState(undefined);
  const urlRef = useRef();
  const descriptionRef = useRef();
  const [photos, setPhotos] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState({
    url: '', // Default empty string for URL
    description: '', // Default empty string for description
  });
  let { movieId } = useParams(); // Get the movieId from the URL

  // Fetch all photos for a movie
  const getAll = useCallback((movieId) => {
    axios({
      method: 'get',
      url: `/movies/${movieId}/photos`, // Correct endpoint
      headers: {
        Accept: 'application/json',
        Authorization: `Bearer ${auth.accessToken}`,
      },
    })
      .then((response) => {
        setPhotos(response.data.photos);
      })
      .catch((error) => {
        console.error('Error fetching Photos:', error.response?.data || error.message);
      });
  }, [auth.accessToken]);

  useEffect(() => {
    getAll(movieId); // Fetch photos on mount or when movieId changes
  }, [movieId, getAll]);

  const validateField = (fieldRef, fieldName) => {
    if (!fieldRef.current.value.trim()) {
      fieldRef.current.style.border = '2px solid red';
      setTimeout(() => {
        fieldRef.current.style.border = '1px solid #ccc';
      }, 2000);
      console.log(`${fieldName} cannot be empty.`);
      return false;
    }
    return true;
  };

  const handleSave = async () => {
    const validateFields = () => {
      const isUrlValid = validateField(urlRef, 'URL');
      const isDescriptionValid = validateField(descriptionRef, 'Description');
      return isUrlValid && isDescriptionValid;
    };

    if (!validateFields()) {
      return; // Stop if any field is invalid
    } else {
      try {
        // Log the selectedPhoto state before saving
        console.log('Selected Photo before saving:', selectedPhoto);

        const dataPhoto = {
          userId: auth.user.userId,
          movieId: movieId,
          url: selectedPhoto.url,
          description: selectedPhoto.description,
        };

        // Log data before saving
        console.log('Data to be saved:', dataPhoto);

        await axios({
          method: 'POST',
          url: '/photos',
          data: dataPhoto,
          headers: {
            Authorization: `Bearer ${auth.accessToken}`,
          },
        });
        alert('Photo added successfully');
        setSelectedPhoto({ url: '', description: '' }); // Reset after save
        getAll(movieId); // Reload photos after saving
      } catch (error) {
        console.error('Error Saving Photo:', error.response?.data || error.message);
      }
    }
  };

  const handleDelete = (id) => {
    const isConfirm = window.confirm('Are you sure you want to delete this photo?');

    if (isConfirm) {
      axios({
        method: 'DELETE',
        url: `/photos/${id}`, // Correct delete endpoint
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      })
        .then(() => {
          alert('Photo deleted successfully');
          getAll(movieId); // Reload photos after deletion
        })
        .catch((err) => {
          console.error('Error Deleting Photo:', err.response?.data || err.message);
        });
    }
  };

  const handleClear = useCallback(() => {
    setSelectedPhoto({ url: '', description: '' });
    setPhotoId(undefined);
  }, []);

  const photoFetch = async (id) => {
    axios({
      method: 'get',
      url: `/photos/${id}`, // Fetch specific photo
      headers: {
        Accept: 'application/json',
        Authorization: `Bearer ${auth.accessToken}`,
      },
    })
      .then((response) => {
        setSelectedPhoto(response.data);
        setPhotoId(response.data.id);
      })
      .catch((error) => {
        console.log('Error fetching photo:', error);
      });
  };

  const photoUpdate = async (id) => {
    const validateFields = () => {
      const isUrlValid = validateField(urlRef,      'URL');
      const isDescriptionValid = validateField(descriptionRef, 'Description');
      return isUrlValid && isDescriptionValid;
    };

    if (!validateFields()) {
      return; // Stop if any field is invalid
    } else {
      const isConfirm = window.confirm('Are you sure you want to update the photo?');
      if (isConfirm) {
        const dataPhoto = {
          userId: auth.user.userId,
          movieId: selectedPhoto.movieId,
          description: selectedPhoto.description,
          url: selectedPhoto.url,
        };

        try {
          const response = await axios({
            method: 'PATCH',
            url: `/photos/${id}`, // Update photo endpoint
            data: dataPhoto,
            headers: {
              Accept: 'application/json',
              Authorization: `Bearer ${auth.accessToken}`,
            },
          });
          console.log(response.data);
          alert('Photo updated successfully!');
          handleClear(); // Clear the form after updating
          getAll(movieId); // Reload photos after update
        } catch (error) {
          console.error('Error updating photo:', error);
        }
      }
    }
  };

  return (
    <div className='photo-box'>
      <div className='Photo-View-Box'>
        {photos && photos.length > 0 ? (
          <div className='card-display-photo'>
            {photos.map((image) => (
              <div key={image.id} className='card-photo'>
                <div className='buttons-group'>
                  <button
                    type='button'
                    className='delete-button'
                    onClick={() => handleDelete(image.id)}
                  >
                    Delete
                  </button>
                  <button
                    type='button'
                    className='edit-button'
                    onClick={() => photoFetch(image.id)}
                  >
                    Edit
                  </button>
                </div>
                <img
                  src={image.url}
                  alt={image.description}
                  style={{ width: '100%' }}
                  className='image-style'
                />
                <div className='container-photo'>
                  <p>{image.description}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className='no-photo'>
            <h3>No photos found</h3>
          </div>
        )}
      </div>

      {/* Photo details input form */}
      <div className='Photo-Search-Box'>
        <div className='parent-container'>
          <div className='photo-detail-box'>
            <div className='photo-container-center'>
              <div className='photo-image-container'>
                <img
                  alt='photo-movies'
                  src={
                    selectedPhoto.url
                      ? selectedPhoto.url
                      : 'https://upload.wikimedia.org/wikipedia/commons/1/14/No_Image_Available.jpg'
                  }
                  className='photo-img'
                />
              </div>
            </div>
          </div>
          <div className='photo-info-text'>
            <div className='input-group'>
              <label className='label-photo'>URL Image:</label>
              <input
                className='photo-url'
                value={selectedPhoto.url || ''}
                onChange={(e) => setSelectedPhoto({ ...selectedPhoto, url: e.target.value })}
                ref={urlRef}
              />
            </div>
            <div className='input-group'>
              <label className='label-photo'>Description:</label>
              <textarea
                className='photo-description'
                value={selectedPhoto.description || ''}
                onChange={(e) => setSelectedPhoto({ ...selectedPhoto, description: e.target.value })}
                ref={descriptionRef}
              />
            </div>
          </div>

          <div className='save-edit-back-btn'>
            {!photoId ? (
              <>
                <button
                  className='edit-save-btn'
                  type='button'
                  onClick={handleSave}
                >
                  Save
                </button>
              </>
            ) : (
              <>
                <button
                  className='edit-save-btn'
                  type='button'
                  onClick={() => photoUpdate(photoId)}
                >
                  Update
                </button>
              </>
            )}
            <button
              className='clear-btn'
              type='button'
              onClick={handleClear}
            >
              Clear
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Photos;
