import React, { useContext, useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import { useParams } from 'react-router-dom';
import './Photos.css';

function Form({ data, state, setState }) {
  const { auth } = useContext(AuthContext);
  const { tmdbId } = useParams();
  
  const [selectedData, setSelectedData] = useState(data || {});
  const [file, setFile] = useState(null);
  const descriptionRef = useRef();
  const urlRef = useRef();
  
  const [alertMessage, setAlertMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [status, setStatus] = useState('idle');

  useEffect(() => {
    if (data) setSelectedData(data);
  }, [data]);

  // Handle file input change
  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  // Handle field changes
  const handleChange = (e) => {
    setSelectedData((prevData) => ({
      ...prevData,
      [e.target.name]: e.target.value,
    }));
  };

  // Save photo (POST request)
  const handleSave = async () => {
    const formData = new FormData();
    formData.append('userId', selectedData.userId);
    formData.append('movieId', tmdbId);
    formData.append('description', selectedData.description);
    formData.append('url', selectedData.url);

    if (file) formData.append('image', file);

    try {
      const response = await axios.post('/photos', formData, {
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });
      setAlertMessage(response.data.message);
      setIsError(false);
      setTimeout(() => {
        setAlertMessage('');
        setState('base');
      }, 2000);
    } catch (error) {
      setAlertMessage('Error saving photo');
      setIsError(true);
      console.error(error);
    }
  };

  // Update photo (PATCH request)
  const handleUpdate = async () => {
    const formData = new FormData();
    formData.append('id', selectedData.id);
    formData.append('userId', selectedData.userId);
    formData.append('movieId', tmdbId);
    formData.append('description', selectedData.description);
    formData.append('url', selectedData.url);

    if (file) formData.append('image', file);

    try {
      const response = await axios.patch(`/photos/${selectedData.id}`, formData, {
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });
      setAlertMessage(response.data.message);
      setIsError(false);
      setTimeout(() => {
        setAlertMessage('');
        setState('base');
      }, 2000);
    } catch (error) {
      setAlertMessage('Error updating photo');
      setIsError(true);
      console.error(error);
    }
  };

  // Delete photo (DELETE request)
  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this photo?')) {
      setStatus('loading');
      axios.delete(`/photos/${selectedData.id}`, {
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      })
        .then((response) => {
          setAlertMessage(response.data.message);
          setStatus('idle');
          setTimeout(() => {
            setAlertMessage('');
            setState('base');
          }, 2000);
        })
        .catch((error) => {
          setAlertMessage('Error deleting photo');
          setIsError(true);
          console.error(error);
        });
    }
  };

  return (
    <>
      {alertMessage && (
        <div className={`alert-box ${isError ? 'error' : 'success'}`}>{alertMessage}</div>
      )}
      <form>
        <div className="field">
          <label>Image URL</label>
          <input
            type="text"
            name="url"
            value={selectedData.url}
            onChange={handleChange}
            ref={urlRef}
          />
        </div>
        <div className="field">
          <label>Description</label>
          <textarea
            rows="5"
            name="description"
            value={selectedData.description}
            onChange={handleChange}
            ref={descriptionRef}
          />
        </div>
        <div className="field">
          <label>Upload Image</label>
          <input type="file" onChange={handleFileChange} />
        </div>
      </form>
      <div className="button-group">
        {state === 'update' && <button onClick={handleDelete}>Delete</button>}
        <button onClick={() => (status === 'loading' ? null : state === 'update' ? handleUpdate() : handleSave())}>
          {state === 'update' ? 'Update' : 'Save'}
        </button>
      </div>
    </>
  );
}

export default Form;
