import React, { useEffect, useRef } from 'react';

function VideosForm({ videoData, onSubmit, isSaving, onChange, editingVideo, urlRef, descriptionRef }) {
  useEffect(() => {
    if (editingVideo) {
      urlRef.current.value = videoData.url;
      descriptionRef.current.value = videoData.description;
    }
  }, [editingVideo, videoData, urlRef, descriptionRef]);

  return (
    <div className="Video-Search-Box">
      <input
        ref={urlRef}
        type="text"
        name="url"
        placeholder="Video URL"
        value={videoData.url}
        onChange={onChange}
        className="video-search-input"
      />
      <textarea
        ref={descriptionRef}
        name="description"
        placeholder="Video Description"
        value={videoData.description}
        onChange={onChange}
        className="video-search-input"
      ></textarea>
      <div className="video-search-buttons">
        <button className="search-btn" onClick={onSubmit} disabled={isSaving}>
          {isSaving ? 'Saving...' : editingVideo ? 'Update Video' : 'Add Video'}
        </button>
      </div>
    </div>
  );
}

export default VideosForm;
