import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import { useParams } from 'react-router-dom';
import Form from './Form';
import './Photos.css';

function Photos() {
  const { auth } = useContext(AuthContext);
  const { tmdbId } = useParams();
  const [photoData, setPhotoData] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState();
  const [state, setState] = useState('base');
  const [alertMessage, setAlertMessage] = useState('');
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    getAll();
  }, [state]);

  // Fetch all photos
  function getAll() {
    axios({
      method: 'get',
      url: `/photos`,
      headers: {
        Accept: 'application/json',
        Authorization: `Bearer ${auth.accessToken}`,
      },
    })
      .then((response) => {
        setPhotoData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("Error fetching photos:", error);
      });
  }

  // Import photos from TMDB
  function importPhotos() {
    axios({
      method: 'get',
      url: `https://api.themoviedb.org/3/movie/${tmdbId}/images`,
      headers: {
        Accept: 'application/json',
        Authorization: 'Bearer YOUR_API_KEY', // Replace with actual key
      },
    })
      .then((response) => {
        saveImportedPhotos(response.data.backdrops);
        setAlertMessage(`Successfully Imported ${response.data.backdrops.length} Photos`);
        setTimeout(() => {
          setAlertMessage('');
          getAll();
        }, 2000);
      })
      .catch((error) => {
        console.error("Error importing photos:", error);
      });
  }

  // Save imported photos to the database
  async function saveImportedPhotos(importedData) {
    await Promise.all(importedData.map(async (data) => {
      const payload = {
        userId: auth.user.userId,
        movieId: tmdbId,
        url: `https://image.tmdb.org/t/p/w500/${data.file_path}`,
        description: `Imported from TMDB`,
      };

      try {
        const response = await axios.post('/photos', payload, {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer ${auth.accessToken}`,
          },
        });
        console.log('Response:', response);
      } catch (error) {
        console.error('Error saving imported photo:', error);
      }
    }));
  }

  // Handle photo deletion
  const handleDelete = (id) => {
    const isConfirmed = window.confirm('Are you sure you want to delete this photo?');
    if (isConfirmed) {
      axios({
        method: 'delete',
        url: `/photos/${id}`,
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      })
        .then((response) => {
          console.log('Database Updated');
          setAlertMessage(response.data.message);
          setTimeout(() => {
            setAlertMessage('');
            setState('base');
            getAll();
          }, 2000);
        })
        .catch((error) => {
          setAlertMessage('Error deleting photo');
          console.error(error);
        });
    }
  };

  return (
    <>
      <div className='photo-header'>
        {alertMessage && (
          <div className={`alert-box ${isError ? 'error' : 'success'}`}>{alertMessage}</div>
        )}
        <h2 onClick={() => setState('base')}>
          {state !== 'base' ? (
            <span className='back-button fas fa-chevron-left'>
              <h3>Back to Photos</h3>
            </span>
          ) : (
            'Photos'
          )}
        </h2>
        <div>
          <button onClick={importPhotos}>IMPORT PHOTOS</button>
          {state === 'base' && <button onClick={() => setState('add')}>ADD PHOTO</button>}
        </div>
      </div>

      {state === 'add' && <Form state={state} setState={setState} />}
      {state === 'update' && <Form data={selectedPhoto} state={state} setState={setState} />}
      {state === 'base' && (
        <div className="photo-cards-container">
          <div className="photo-cards-group">
            {photoData?.map((photo) => (
              photo.movieId === parseInt(tmdbId) && (
                <div key={photo.id} className="photo-card" onClick={() => { setSelectedPhoto(photo); setState('update') }}>
                  <div className='control-group'>
                    <span onClick={() => { setSelectedPhoto(photo); setState('update') }} className='fas fa-edit'></span>
                    <span onClick={() => { handleDelete(photo.id) }} className='fas fa-trash-can'></span>
                  </div>
                  <img
                    src={photo.url ? photo.url : 'https://via.placeholder.com/500x750?text=No+Image'}
                    alt={photo.description}
                    className="photo-image"
                  />
                </div>
              )
            ))}
          </div>
        </div>
      )}
    </>
  );
}

export default Photos;
