// Videos.jsx
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import { useParams } from 'react-router-dom';
import VideosForm from './VideosForm'; // Import VideosForm
import './Videos.css';

function Videos() {
  const { auth } = useContext(AuthContext);
  const { movieId } = useParams();
  const [videos, setVideos] = useState([]);
  const [newVideo, setNewVideo] = useState({ url: '', description: '' });
  const [editingVideo, setEditingVideo] = useState(null);
  const [isSaving, setIsSaving] = useState(false);

  // Fetch videos associated with the current movie
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get(`/movies/${movieId}/videos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });

        if (Array.isArray(response.data.videos)) {
          setVideos(response.data.videos);
        } else {
          console.error("Error: No videos found or videos is not an array");
          setVideos([]);
        }
      } catch (error) {
        console.error("Error fetching videos:", error);
        setVideos([]);
      }
    };

    fetchVideos();
  }, [movieId, auth.accessToken]);

  // Helper function to extract YouTube video ID from URL
  const getYouTubeVideoId = (url) => {
    if (!url || typeof url !== 'string' || url.trim() === '') {
      console.error("URL is undefined, empty, or not a valid string:", url);
      return null;
    }

    const regex = /(?:https?:\/\/)?(?:www\.)?(?:youtube\.com\/(?:[^\/]+\/\S+\/|(?:v|e(?:mbed)?)\/))?(?:watch\?v=|e(?:mbed)\/)([\w-]{11})/;
    const match = url.match(regex);
    return match ? match[1] : null;
  };

  const handleAddVideo = async () => {
    // Check if URL and description are not empty
    if (!newVideo.url || !newVideo.description) {
      alert("Please provide both a URL and a description for the video.");
      return;
    }
  
    // Log the URL before processing
    console.log("URL to be processed:", newVideo.url);
  
    // Validate URL
    const videoId = getYouTubeVideoId(newVideo.url);
  
    if (!videoId) {
      alert("Please provide a valid YouTube URL.");
      return;
    }
  
    setIsSaving(true);
    try {
      const videoData = {
        movieId,
        url: newVideo.url,
        description: newVideo.description,
        userId: auth.user.userId,
      };
  
      const response = await axios.post('/videos', videoData, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });
  
      // Add the new video to the list
      setVideos((prevVideos) => [...prevVideos, response.data]);
  
      // Optionally clear the form if you want
      setNewVideo({ url: '', description: '' });
  
      alert("Video added successfully!");
  
      // Refresh the page after successfully adding the video
      window.location.reload(); // This will reload the page
  
    } catch (error) {
      console.error("Error adding video:", error);
      alert("Failed to add video.");
    } finally {
      setIsSaving(false);
    }
  };

  const handleDeleteVideo = async (id) => {
    if (window.confirm("Are you sure you want to delete this video?")) {
      try {
        await axios.delete(`/videos/${id}`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });

        setVideos((prevVideos) => prevVideos.filter((video) => video.id !== id));
        alert("Video deleted successfully!");
      } catch (error) {
        console.error("Error deleting video:", error);
        alert("Failed to delete video.");
      }
    }
  };

  const handleUpdateVideo = async () => {
    // Helper function to validate fields
    const validateFields = () => {
      const isUrlValid = newVideo.url && getYouTubeVideoId(newVideo.url);
      if (!isUrlValid) {
        alert("Please provide a valid YouTube URL.");
        return false;
      }
  
      const isDescriptionValid = newVideo.description && newVideo.description.trim() !== '';
      if (!isDescriptionValid) {
        alert("Description is required.");
        return false;
      }
  
      return true;
    };
  
    if (!validateFields()) {
      return; // Stop if validation fails
    }
  
    // Confirmation before updating
    const isConfirm = window.confirm("Are you sure you want to update this video?");
    if (!isConfirm) {
      return;
    }
  
    const videoId = getYouTubeVideoId(newVideo.url);
    if (!videoId) {
      alert("Invalid YouTube link. Please enter a valid URL.");
      return;
    }
  
    const data = {
      url: newVideo.url,
      description: newVideo.description,
      videoKey: videoId, // Assuming the `videoKey` is needed
    };
  
    setIsSaving(true);
  
    try {
      const response = await axios.patch(`/videos/${editingVideo.id}`, data, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });
  
      // Update the videos list with the updated video data
      setVideos((prevVideos) =>
        prevVideos.map((video) =>
          video.id === editingVideo.id ? { ...video, ...data } : video
        )
      );
  
      setEditingVideo(null); // Reset editing state
      alert("Video updated successfully!");
      console.log(response.data); // Log the response for debugging
    } catch (error) {
      console.error("Error updating video:", error.response?.data || error.message);
      alert("Failed to update video.");
    } finally {
      setIsSaving(false);
    }
  };
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingVideo) {
      setEditingVideo((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewVideo((prev) => ({ ...prev, [name]: value }));
    }
  };

  return (
    <div className="video-box">
      <div className="Video-View-Box">
        <h2>Videos for Movie</h2>

        <div className="card-display-videos">
          {videos.length === 0 ? (
            <p>No videos found for this movie.</p>
          ) : (
            videos.map((video) => {
              const videoId = getYouTubeVideoId(video.url);
              return (
                <div key={video.id} className="card-video">
                  {videoId ? (
                    <iframe
                      width="100%"
                      height="315"
                      src={`https://www.youtube.com/embed/${videoId}`}
                      title="Video"
                      frameBorder="0"
                      allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                    ></iframe>
                  ) : (
                    <p>Invalid video URL</p>
                  )}
                  <div className="container-video">
                    <h4>{video.description}</h4>
                    <p>Added on: {new Date(video.dateCreated).toLocaleDateString()}</p>
                    <div className="buttons-group">
                      <button className="edit-button" onClick={() => setEditingVideo(video)}>
                        Edit
                      </button>
                      <button className="delete-button" onClick={() => handleDeleteVideo(video.id)}>
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>

      <VideosForm
        videoData={editingVideo ? editingVideo : newVideo}
        onSubmit={editingVideo ? handleUpdateVideo : handleAddVideo}
        onChange={handleInputChange}
        isSaving={isSaving}
        editingVideo={editingVideo}
      />
    </div>
  );
}

export default Videos;
