import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import { useHistory } from 'react-router-dom';
import './MovieForm.css';

function MovieForm() {
  const { auth } = useContext(AuthContext);
  const [query, setQuery] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [casts, setCasts] = useState([]);
  const [videos, setVideos] = useState([]);
  const [photos, setPhotos] = useState([]);
  const [isSaving, setIsSaving] = useState(false);
  const history = useHistory();

  // Fetch movie, casts, videos, photos when a movie is selected
  useEffect(() => {
    if (!selectedMovie) return;

    const fetchRelatedData = async () => {
      try {
        // Fetch cast
        const castResponse = await axios.get(`/movies/${selectedMovie.id}/casts`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setCasts(castResponse.data);

        // Fetch videos
        const videoResponse = await axios.get(`/movies/${selectedMovie.id}/videos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setVideos(videoResponse.data);

        // Fetch photos
        const photoResponse = await axios.get(`/movies/${selectedMovie.id}/photos`, {
          headers: { Authorization: `Bearer ${auth.accessToken}` },
        });
        setPhotos(photoResponse.data);
      } catch (error) {
        console.error('Error fetching related data', error);
      }
    };

    fetchRelatedData();
  }, [selectedMovie, auth.accessToken]);

  // Search for a movie by name
  const handleSearch = async () => {
    if (!query.trim()) return;

    try {
      const response = await axios.get(`/search/movie?query=${query}`, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });
      setSelectedMovie(response.data.results[0]);  // Just select the first result for simplicity
    } catch (error) {
      console.error('Error searching movie', error);
    }
  };

  // Handle save logic
  const handleSave = async () => {
    if (!selectedMovie) {
      alert('No movie selected!');
      return;
    }

    setIsSaving(true);
    try {
      const movieData = {
        ...selectedMovie,
        casts,
        videos,
        photos,
        userId: auth.user.userId,
      };

      await axios.post('/movies', movieData, {
        headers: { Authorization: `Bearer ${auth.accessToken}` },
      });

      alert('Movie saved successfully');
      history.push('/movies');  // Redirect to movie list page after save
    } catch (error) {
      console.error('Error saving movie', error);
      alert('Error saving movie!');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="movie-form">
      <h1>Movie Form</h1>

      {/* Search Movie */}
      <div>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Display selected movie details */}
      {selectedMovie && (
        <div className="movie-details">
          <h2>{selectedMovie.title}</h2>
          <p>{selectedMovie.overview}</p>
        </div>
      )}

      {/* Render Cast, Videos, and Photos automatically once movie is selected */}
      <div className="cast-box">
        <h3>Cast & Crew</h3>
        {casts.length > 0 ? (
          <div className="card-display-cast">
            {casts.map((actor) => (
              <div key={actor.id} className="card">
                <img src={actor.url} alt={actor.name} className="image-casts" />
                <h4>{actor.name}</h4>
                <p>{actor.characterName}</p>
              </div>
            ))}
          </div>
        ) : (
          <div>No cast found</div>
        )}
      </div>

      <div className="video-box">
        <h3>Videos</h3>
        {videos.length > 0 ? (
          <div className="card-display-videos">
            {videos.map((video) => (
              <div key={video.id} className="card-video">
                <iframe
                  src={`https://www.youtube.com/embed/${video.videoId}`}
                  title="Video"
                />
                <h4>{video.description}</h4>
              </div>
            ))}
          </div>
        ) : (
          <div>No videos found</div>
        )}
      </div>

      <div className="photos-box">
        <h3>Photos</h3>
        {photos.length > 0 ? (
          <div className="card-display-photos">
            {photos.map((photo) => (
              <div key={photo.id} className="photo-card">
                <img src={photo.url} alt="Movie Photo" />
              </div>
            ))}
          </div>
        ) : (
          <div>No photos found</div>
        )}
      </div>

      {/* Save Button */}
      <button onClick={handleSave} disabled={isSaving}>
        {isSaving ? 'Saving...' : 'Save Movie'}
      </button>
    </div>
  );
}

export default MovieForm;
