import React, { useContext, useEffect, useState } from 'react';
import './Videos.css';
import { useNavigate, useParams } from 'react-router-dom';
import { AppContext } from '../../../../../context/AppContext';
import axios from 'axios';

function Videos() {
  const { auth } = useContext(AppContext);
  let { tmdbId } = useParams();
  let { movieId } = useParams();

  const [videoData, setVideoData] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState();

  const [state, setState] = useState('base');  // base, add, update

  // Alert box state
  const [alertMessage, setAlertMessage] = useState('');
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    getAll();
  }, [state]);

  function getAll() {
    axios({
      method: 'get',
      url: `/videos`,
      headers: {
        Accept: 'application/json',
        Authorization: `Bearer ${auth.accessToken}`,
      },
    }).then((response) => {
      setVideoData(response.data);
    }).catch((error) => {
      console.error(error);
    });
  }

  function importVideos() {
    axios({
      method: 'get',
      url: `https://api.themoviedb.org/3/movie/${tmdbId}/videos`,
      headers: {
        Accept: 'application/json',
        Authorization: 'Bearer YOUR_API_KEY', // Make sure to replace this with your actual API key
      },
    }).then((response) => {
      saveImportedVideos(response.data.results);
      setIsError(false);
      setAlertMessage(`Successfully Imported ${response.data.results.length} Videos`);
      setTimeout(() => {
        setAlertMessage('');
        getAll();
      }, 2000);
    }).catch((error) => {
      setIsError(true);
      setAlertMessage('Failed to import videos');
      setTimeout(() => setAlertMessage(''), 2000);
    });
  }

  async function saveImportedVideos(importedData) {
    await Promise.all(importedData.map(async (data) => {
      const payload = {
        userId: auth.user.userId,
        movieId: tmdbId,
        url: `https://youtube.com/embed/${data.key}`,
        description: `Imported from TMDB`,
      };
      try {
        const response = await axios.post('/videos', payload, {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer ${auth.accessToken}`,
          },
        });
        console.log('Response:', response);
      } catch (error) {
        console.error('Error saving video:', error);
      }
    }));
  }

  const handleDelete = (id) => {
    const isConfirmed = window.confirm('Are you sure you want to delete this Video?');
    if (isConfirmed) {
      axios({
        method: 'delete',
        url: `/videos/${id}`,
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      }).then((response) => {
        setIsError(false);
        setAlertMessage(response.data.message);
        setTimeout(() => {
          setAlertMessage('');
          setState('base');
          getAll();
        }, 2000);
      }).catch((error) => {
        setIsError(true);
        setAlertMessage(error.response?.data?.message || 'An error occurred');
        setTimeout(() => setAlertMessage(''), 2000);
      });
    }
  };

  return (
    <>
      <div className='video-header'>
        {alertMessage && (
          <div className={`alert-box ${isError ? 'error' : 'success'}`}>{alertMessage}</div>
        )}
        <h2 onClick={() => setState('base')}>
          {state !== 'base' ? (
            <span className='back-button fas fa-chevron-left'>
              <h3>Back to Videos</h3>
            </span>
          ) : (
            'Videos'
          )}
        </h2>
        <div>
          <button onClick={importVideos}>IMPORT VIDEOS</button>
          {state === 'base' && <button onClick={() => setState('add')}>ADD VIDEO</button>}
        </div>
      </div>

      {state === 'add' && <Form data={[]} state={state} setState={setState} />}
      {state === 'update' && <Form data={selectedVideo} state={state} setState={setState} />}

      {state === 'base' && (
        <div className='video-cards-container'>
          <div className='video-cards-group'>
            {videoData?.map(
              (video) =>
                video.movieId === parseInt(tmdbId) && (
                  <div key={video.id} className='video-card' onClick={() => setSelectedVideo(video)}>
                    <div className='control-group'>
                      <span
                        onClick={() => {
                          setSelectedVideo(video);
                          setState('update');
                        }}
                        className='fas fa-edit'
                      ></span>
                      <span
                        onClick={() => {
                          handleDelete(video.id);
                        }}
                        className='fas fa-trash-can'
                      ></span>
                    </div>
                    <iframe
                      src={video.url && !video.url.includes('null') ? video.url : 'https://via.placeholder.com/500x300?text=No+Video'}
                      className='video-frame'
                      allowFullScreen
                    ></iframe>
                  </div>
                )
            )}
          </div>
        </div>
      )}
    </>
  );
}

export default Videos;
