import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import './Videos.css';
import { useParams } from 'react-router-dom';

function Videos() {
    const { auth } = useContext(AuthContext);
    const { movieId } = useParams(); // Get movieId from the URL
    const [videos, setVideos] = useState([]); // Ensure this is initialized as an array
    const [newVideo, setNewVideo] = useState({ url: '', description: '' });
    const [editingVideo, setEditingVideo] = useState(null); // To track video being edited
    const [isSaving, setIsSaving] = useState(false);

    useEffect(() => {
        // Fetch videos associated with the current movie
        const fetchVideos = async () => {
            try {
                const response = await axios.get(`/movies/${movieId}/videos`, {
                    headers: { Authorization: `Bearer ${auth.accessToken}` },
                });

                // Log the response to see what structure is returned
                console.log('Fetched videos:', response.data);

                // Check if response data is an array, otherwise initialize it as an empty array
                if (Array.isArray(response.data)) {
                    setVideos(response.data);
                } else {
                    setVideos([]); // Fallback to empty array if the response is not an array
                }
            } catch (error) {
                console.error("Error fetching videos:", error);
                setVideos([]); // Fallback to empty array if an error occurs
            }
        };

        fetchVideos();
    }, [movieId, auth.accessToken]);

    // Handle adding a new video
    const handleAddVideo = async () => {
        setIsSaving(true);
        try {
            const videoData = {
                movieId,
                url: newVideo.url,
                description: newVideo.description,
                userId: auth.user.userId,
            };

            const response = await axios.post('\admin/videos', videoData, {
                headers: { Authorization: `Bearer ${auth.accessToken}` },
            });

            setVideos((prevVideos) => [...prevVideos, response.data]);
            setNewVideo({ url: '', description: '' }); // Clear input fields
            alert("Video added successfully!");
        } catch (error) {
            console.error("Error adding video:", error);
            alert("Failed to add video.");
        } finally {
            setIsSaving(false);
        }
    };

    // Handle deleting a video
    const handleDeleteVideo = async (id) => {
        if (window.confirm("Are you sure you want to delete this video?")) {
            try {
                await axios.delete(`/videos/${id}`, {
                    headers: { Authorization: `Bearer ${auth.accessToken}` },
                });

                setVideos((prevVideos) => prevVideos.filter((video) => video.id !== id));
                alert("Video deleted successfully!");
            } catch (error) {
                console.error("Error deleting video:", error);
                alert("Failed to delete video.");
            }
        }
    };

    // Handle updating a video
    const handleUpdateVideo = async () => {
        setIsSaving(true);
        try {
            const updatedData = {
                url: editingVideo.url,
                description: editingVideo.description,
            };

            await axios.patch(`/videos/${editingVideo.id}`, updatedData, {
                headers: { Authorization: `Bearer ${auth.accessToken}` },
            });

            setVideos((prevVideos) =>
                prevVideos.map((video) =>
                    video.id === editingVideo.id ? { ...video, ...updatedData } : video
                )
            );
            setEditingVideo(null);
            alert("Video updated successfully!");
        } catch (error) {
            console.error("Error updating video:", error);
            alert("Failed to update video.");
        } finally {
            setIsSaving(false);
        }
    };

    // Handle input change for new video or editing video
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (editingVideo) {
            setEditingVideo((prev) => ({ ...prev, [name]: value }));
        } else {
            setNewVideo((prev) => ({ ...prev, [name]: value }));
        }
    };

    return (
        <div className="videos-container">
            <h2>Videos for Movie</h2>

            {/* Add Video Form */}
            <div className="video-form">
                <input
                    type="text"
                    name="url"
                    placeholder="Video URL"
                    value={editingVideo ? editingVideo.url : newVideo.url}
                    onChange={handleInputChange}
                />
                <textarea
                    name="description"
                    placeholder="Video Description"
                    value={editingVideo ? editingVideo.description : newVideo.description}
                    onChange={handleInputChange}
                ></textarea>
                <button onClick={editingVideo ? handleUpdateVideo : handleAddVideo} disabled={isSaving}>
                    {isSaving ? 'Saving...' : editingVideo ? 'Update Video' : 'Add Video'}
                </button>
            </div>

            {/* Display existing videos */}
            <div className="videos-list">
                {Array.isArray(videos) && videos.length === 0 ? (
                    <p>No videos found for this movie.</p>
                ) : (
                    Array.isArray(videos) &&
                    videos.map((video) => (
                        <div key={video.id} className="video-item">
                            <div className="video-info">
                                <iframe
                                    width="560"
                                    height="315"
                                    src={video.url}
                                    title="Video"
                                    frameBorder="0"
                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                    allowFullScreen
                                ></iframe>
                                <p>{video.description}</p>
                                <small>Added on: {new Date(video.dateCreated).toLocaleDateString()}</small>
                            </div>
                            <div className="video-actions">
                                <button onClick={() => setEditingVideo(video)}>Edit</button>
                                <button onClick={() => handleDeleteVideo(video.id)}>Delete</button>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default Videos;
