import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PhotosForm({ data, state, setState }) {
  const [formData, setFormData] = useState({
    url: data?.url || '',
    description: data?.description || '',
  });
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    if (state === 'update' && data) {
      setFormData({ url: data.url, description: data.description });
    }
  }, [state, data]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.url || !formData.description) {
      alert("Please provide both a URL and a description.");
      return;
    }

    setIsSaving(true);
    const payload = {
      url: formData.url,
      description: formData.description,
    };

    try {
      if (state === 'add') {
        // Add new photo
        await axios.post('/photos', payload, {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer YOUR_ACCESS_TOKEN`,
          },
        });
        alert("Photo added successfully!");
      } else if (state === 'update') {
        // Update existing photo
        await axios.patch(`/photos/${data.id}`, payload, {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer YOUR_ACCESS_TOKEN`,
          },
        });
        alert("Photo updated successfully!");
      }
      setState('base'); // Return to the photo listing view
    } catch (error) {
      console.error('Error saving photo:', error);
      alert('Failed to save photo.');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="photo-form">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="url">Photo URL</label>
          <input
            type="text"
            id="url"
            name="url"
            value={formData.url}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <input
            type="text"
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-actions">
          <button type="submit" disabled={isSaving}>
            {isSaving ? 'Saving...' : state === 'add' ? 'Add Photo' : 'Update Photo'}
          </button>
          <button type="button" onClick={() => setState('base')}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default PhotosForm;
