import * as React from 'react';
import * as ReactDOM from 'react-dom/client';
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import './index.css';
import Login from './pages/Public/Login/Login';
import Dashboard from './pages/Main/Dashboard/Dashboard';
import Main from './pages/Main/Main';
import Movie from './pages/Main/Movie/Movie';
import Lists from './pages/Main/Movie/Lists/Lists';
import Form from './pages/Main/Movie/Form/Form';
import Cast from './pages/Main/Movie/Cast/Cast';  // Import the Cast component

const router = createBrowserRouter([
  {
    path: '/',
    element: <Login />,
  },
  {
    path: '/main',
    element: <Main />,
    children: [
      // Temporarily disabled the dashboard route
      // {
      //   path: '/main/dashboard',
      //   element: <Dashboard />,
      // },
      {
        path: '/main/movies',
        element: <Movie />,
        children: [
          {
            path: '/main/movies',
            element: <Lists />,
          },
          {
            path: '/main/movies/form/:movieId?',
            element: <Form />,
            children: [
              {
                path: '/main/movies/form/:movieId',
                element: (
                  <h1>Change this for cast & crew CRUD functionality.</h1>
                ),
              },
              {
                path: '/main/movies/form/:movieId/cast-and-crews',  // New route for Cast & Crew functionality
                element: <Cast />,  // Add Cast component here
              },
              {
                path: '/main/movies/form/:movieId/photos',
                element: (
                  <h1>Change this for photos CRUD functionality component.</h1>
                ),
              },
              {
                path: '/main/movies/form/:movieId/videos',
                element: (
                  <h1>Change this for videos CRUD functionality component.</h1>
                ),
              },
            ],
          },
        ],
      },
    ],
  },
]);

function App() {
  return (
    <div className='App'>
      <RouterProvider router={router} />
    </div>
  );
}

// app.js or server.js
const express = require('express');
const corsMiddleware = require('./corsMiddleware'); // Import the CORS middleware
const app = express();

// Apply the CORS middleware globally
app.use(corsMiddleware);

// Your routes here
app.get('/movieproject-api/casts/:movieId', (req, res) => {
  // Your logic to fetch cast
  res.json({ message: 'Cast data fetched successfully' });
});

app.post('/movieproject-api/admin/casts', (req, res) => {
  // Your logic to create a new cast
  res.json({ message: 'Cast created successfully' });
});

// Start the server
app.listen(5000, () => {
  console.log('Server is running on port 5000');
});


export default App;
