import React, { useState, useRef, useCallback, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [lastName, setLastName] = useState('');
  const [contactNo, setContactNo] = useState('');
  const [isFieldsDirty, setIsFieldsDirty] = useState(false);
  const emailRef = useRef();
  const passwordRef = useRef();
  const confirmPasswordRef = useRef();
  const [isShowPassword, setIsShowPassword] = useState(false);
  const [status, setStatus] = useState('idle');
  
  // Typing effect state
  const [typedText, setTypedText] = useState('');
  const words = ['Movie', 'Series', 'TV Show', 'Anime'];
  const [wordIndex, setWordIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  const navigate = useNavigate();

  // Typing effect
  useEffect(() => {
    const handleTyping = () => {
      const currentWord = words[wordIndex];
      setTypedText(prev => 
        isDeleting 
          ? prev.slice(0, -1) 
          : currentWord.slice(0, prev.length + 1)
      );

      if (!isDeleting && typedText === currentWord) {
        setTimeout(() => setIsDeleting(true), 2000);
      }

      if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setWordIndex((prev) => (prev + 1) % words.length);
      }
    };

    const typingSpeed = isDeleting ? 50 : 100;
    const timer = setTimeout(handleTyping, typingSpeed);

    return () => clearTimeout(timer);
  }, [typedText, isDeleting, wordIndex]);

  const handleShowPassword = useCallback(() => {
    setIsShowPassword((value) => !value);
  }, []);

  const handleOnChange = (event, type) => {
    setIsFieldsDirty(true);

    switch (type) {
      case 'email': setEmail(event.target.value); break;
      case 'password': setPassword(event.target.value); break;
      case 'confirmPassword': setConfirmPassword(event.target.value); break;
      case 'firstName': setFirstName(event.target.value); break;
      case 'middleName': setMiddleName(event.target.value); break;
      case 'lastName': setLastName(event.target.value); break;
      case 'contactNo': setContactNo(event.target.value); break;
      default: break;
    }
  };

  const handleRegister = async () => {
    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    const data = {
      email,
      password,
      firstName,
      middleName,
      lastName,
      contactNo,
      role: 'admin', 
    };
    setStatus('loading');

    try {
      const response = await axios({
        method: 'POST',
        url: '/admin/register',
        data,
        headers: { 'Access-Control-Allow-Origin': '*' },
      });
      console.log(response);
      navigate('/');
      setStatus('idle');
    } catch (error) {
      console.log(error);
      setStatus('idle');
    }
  };

  return (
    <div className='Register'>
      <div className='main-container'>
        <div className='title-container'>
          <h1>Welcome to CineSphere</h1>
          <p>
            The ultimate online hub for film lovers! Whether you're a casual viewer or a dedicated cinephile, CineSphere is designed to enhance your movie-watching experience by providing a rich database of films from every genre and era.
          </p>
          <div className='typing-animation'>
            Explore Your Favorite <span className='typed-text'>{typedText}</span>
            <span className='cursor'>|</span>
          </div>
        </div>
        <form>
          <div className='form-container'>
            <h3>Register</h3>
            <div className='form-group'>
              <label>E-mail:</label>
              <input
                type='text'
                name='email'
                ref={emailRef}
                onChange={(e) => handleOnChange(e, 'email')}
              />
              {isFieldsDirty && email === '' && <span className='errors'>This field is required</span>}
            </div>

            <div className='form-group'>
              <label>First Name:</label>
              <input
                type='text'
                name='firstName'
                ref={emailRef}
                onChange={(e) => handleOnChange(e, 'firstName')}
              />
              {isFieldsDirty && firstName === '' && <span className='errors'>This field is required</span>}
            </div>

            <div className='form-group'>
              <label>Middle Name:</label>
              <input
                type='text'
                name='middleName'
                ref={emailRef}
                onChange={(e) => handleOnChange(e, 'middleName')}
              />
            </div>

            <div className='form-group'>
              <label>Last Name:</label>
              <input
                type='text'
                name='lastName'
                ref={emailRef}
                onChange={(e) => handleOnChange(e, 'lastName')}
              />
              {isFieldsDirty && lastName === '' && <span className='errors'>This field is required</span>}
            </div>

            <div className='form-group'>
              <label>Contact No:</label>
              <input
                type='text'
                name='contactNo'
                ref={emailRef}
                onChange={(e) => handleOnChange(e, 'contactNo')}
              />
            </div>

            <div className='form-group'>
              <label>Password:</label>
              <input
                type={isShowPassword ? 'text' : 'password'}
                name='password'
                ref={passwordRef}
                onChange={(e) => handleOnChange(e, 'password')}
              />
              {isFieldsDirty && password === '' && <span className='errors'>This field is required</span>}
            </div>

            <div className='form-group'>
              <label>Confirm Password:</label>
              <input
                type={isShowPassword ? 'text' : 'password'}
                name='confirmPassword'
                ref={confirmPasswordRef}
                onChange={(e) => handleOnChange(e, 'confirmPassword')}
              />
              {isFieldsDirty && confirmPassword === '' && <span className='errors'>This field is required</span>}
            </div>

            <div className='show-password' onClick={handleShowPassword} style={{ gridColumn: 'span 2', textAlign: 'center' }}>
              {isShowPassword ? 'Hide' : 'Show'} Password
            </div>

            <div className='submit-container' style={{ gridColumn: 'span 2', textAlign: 'center' }}>
              <button
                type='button'
                disabled={status === 'loading'}
                onClick={() => {
                  if (status === 'loading') {
                    return;
                  }
                  handleRegister();
                }}
              >
                {status === 'idle' ? 'Register' : 'Loading'}
              </button>
            </div>

            <div className='login-container' style={{ gridColumn: 'span 2', textAlign: 'center', marginTop: '20px' }}>
              <a href='/'>
                <small>Already have an account? Login</small>
              </a>
            </div>
          </div>
        </form>
      </div>

      <style jsx>{`
        .typing-animation {
          margin-top: 10px;
          font-size: 1.2rem;
          color: #007BFF;
        }

        .typed-text {
          color: #0056b3;
          font-weight: bold;
        }

        .cursor {
          animation: blink 0.7s infinite;
        }

        @keyframes blink {
          0%, 100% { opacity: 1; }
          50% { opacity: 0; }
        }
      `}</style>
    </div>
  );
}

export default Register;
