import React, { useState } from 'react';
import axios from 'axios';

const CastAndCrew = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [selectedMembers, setSelectedMembers] = useState([]);

    const handleSearch = async () => {
        const API_BASE_URL = 'https://api.themoviedb.org/3';
        const API_KEY = 'YOUR_API_KEY'; // Your TMDB API Key

        try {
            const response = await axios.get(`${API_BASE_URL}/search/person?query=${query}&api_key=${API_KEY}`);
            setResults(response.data.results);
        } catch (error) {
            console.error('Error fetching cast and crew:', error);
        }
    };

    const handleSelectMember = (member) => {
        if (!selectedMembers.some(selected => selected.id === member.id)) {
            setSelectedMembers((prev) => [...prev, member]);
        }
    };

    const handleRemoveMember = (id) => {
        setSelectedMembers((prev) => prev.filter(member => member.id !== id));
    };

    const handleSave = async () => {
        const accessToken = localStorage.getItem('accessToken');
        
        try {
            await Promise.all(selectedMembers.map(member =>
                axios.post('http://localhost/movieproject-api/admin/casts', {
                    name: member.name,
                    profile_path: member.profile_path,
                    characterName: member.characterName // Adjust based on your API structure
                }, {
                    headers: { Authorization: `Bearer ${accessToken}` }
                })
            ));
            alert('Selected cast and crew members saved successfully.');
            setSelectedMembers([]); // Clear the list after saving
        } catch (error) {
            console.error('Error saving members:', error);
            alert('Failed to save some or all members.');
        }
    };

    return (
        <div>
            <h2>Search Cast and Crew</h2>
            <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Enter name..."
            />
            <button onClick={handleSearch}>Search</button>

            <h3>Search Results</h3>
            <ul>
                {results.map(member => (
                    <li key={member.id}>
                        {member.name}
                        <button onClick={() => handleSelectMember(member)}>Select</button>
                    </li>
                ))}
            </ul>

            <h3>Selected Members</h3>
            <ul>
                {selectedMembers.map(member => (
                    <li key={member.id}>
                        {member.name}
                        <button onClick={() => handleRemoveMember(member.id)}>Remove</button>
                    </li>
                ))}
            </ul>

            <button onClick={handleSave}>Save Selected Members</button>
        </div>
    );
};

export default CastAndCrew;
