import axios from 'axios';
import { useState, useCallback, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Cast.css';

const Cast = () => {
  const [query, setQuery] = useState('');
  const [searchedPersonList, setSearchedPersonList] = useState([]);
  const [selectedPerson, setSelectedPerson] = useState(undefined);
  const [addedCast, setAddedCast] = useState([]);
  const [error, setError] = useState(null);
  const { movieId } = useParams(); // Get movieId from URL
  const userId = localStorage.getItem('userId');
  const accessToken = localStorage.getItem('accessToken');

  // TMDB API Token (Replace with your token)
  const TMDB_AUTHORIZATION_TOKEN = `Bearer <YOUR_TMDB_BEARER_TOKEN>`;

  // Backend API URL
  const BACKEND_API_URL = 'http://localhost/movieproject-api/admin/casts';

  // Search for people using TMDB API
  const handleSearch = useCallback(() => {
    if (!query.trim()) {
      alert('Please enter a cast name to search.');
      return;
    }
    axios
      .get(`https://api.themoviedb.org/3/search/person?query=${query}&include_adult=false&language=en-US&page=1`, {
        headers: {
          Authorization: TMDB_AUTHORIZATION_TOKEN,
        },
      })
      .then((response) => {
        setSearchedPersonList(response.data.results || []);
      })
      .catch((error) => {
        console.error('Error fetching TMDB data:', error);
        setError('Failed to fetch cast data. Please try again.');
      });
  }, [query, TMDB_AUTHORIZATION_TOKEN]);

  const handleSelectPerson = (person) => {
    setSelectedPerson(person);
  };

  const handleAddCast = () => {
    if (!selectedPerson) {
      alert('Please select a person to add as cast.');
      return;
    }

    // Check for duplicates
    const alreadyAdded = addedCast.some((cast) => cast.id === selectedPerson.id);
    if (alreadyAdded) {
      alert('This person is already added to the cast.');
      return;
    }

    setAddedCast((prevCast) => [
      ...prevCast,
      {
        id: selectedPerson.id,
        name: selectedPerson.name,
        url: selectedPerson.profile_path
          ? `https://image.tmdb.org/t/p/original${selectedPerson.profile_path}`
          : '',
        characterName: '',
        dateCreated: new Date().toISOString(),
        dateUpdated: new Date().toISOString(),
      },
    ]);
    setSelectedPerson(undefined); // Clear selected person
  };

  // Save the cast list to the backend
  const handleSaveCast = async () => {
    if (addedCast.length === 0) {
      alert('No cast members added.');
      return;
    }

    try {
      await axios.post(
        BACKEND_API_URL,
        { movieId, cast: addedCast },
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
            'Content-Type': 'application/json',
          },
        }
      );
      alert('Cast successfully saved to the database!');
    } catch (error) {
      console.error('Error saving cast:', error.response?.data || error.message);
      alert('Failed to save cast. Please try again.');
    }
  };

  return (
    <div className="cast-and-crew-container">
      <h1>Cast & Crew Management</h1>

      {/* Search Section */}
      <div className="search-container">
        <label>Search Person:</label>
        <input
          type="text"
          value={query}
          onChange={(event) => setQuery(event.target.value)}
          placeholder="Enter cast name"
        />
        <button type="button" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Display TMDB Search Results */}
      {searchedPersonList.length > 0 && (
        <div className="search-results">
          <h3>Search Results:</h3>
          <ul>
            {searchedPersonList.map((person) => (
              <li key={person.id} onClick={() => handleSelectPerson(person)}>
                <img
                  src={`https://image.tmdb.org/t/p/w200${person.profile_path}`}
                  alt={person.name}
                  width={50}
                />
                {person.name}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Display Selected Person */}
      {selectedPerson && (
        <div className="selected-person">
          <h3>Selected Person:</h3>
          <img
            src={`https://image.tmdb.org/t/p/original/${selectedPerson.profile_path}`}
            alt={selectedPerson.name}
            width={100}
          />
          <p>{selectedPerson.name}</p>
          <button onClick={handleAddCast}>Add Cast</button>
        </div>
      )}

      {/* Added Cast */}
      <div className="added-cast">
        <h3>Added Cast:</h3>
        <ul>
          {addedCast.map((cast, index) => (
            <li key={index}>
              <img src={cast.url} alt={cast.name} width={50} />
              {cast.name} as {cast.characterName}
            </li>
          ))}
        </ul>
        <button onClick={handleSaveCast}>Save Cast</button>
      </div>

      {/* Display Error Message */}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default Cast;
