import axios from 'axios';
import { useState, useCallback, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Cast.css';

const Cast = () => {
  const [query, setQuery] = useState(''); // Search query for TMDB
  const [searchedPersonList, setSearchedPersonList] = useState([]); // TMDB results
  const [selectedPerson, setSelectedPerson] = useState(undefined); // Selected person from search
  const [addedCast, setAddedCast] = useState([]); // Cast to save to the backend
  const [error, setError] = useState(null); // Error state for UI
  const { movieId } = useParams(); // Get `movieId` from URL
  const accessToken = localStorage.getItem('accessToken'); // Assuming accessToken is stored in localStorage

  const TMDB_AUTHORIZATION_TOKEN = `Bearer ${accessToken}`;

  // TMDB API base URL
  const TMDB_BASE_URL = 'https://api.themoviedb.org/3';

  // Backend API URL for saving cast
  const BACKEND_API_URL = 'http://localhost/movieproject-api/admin/casts';

  // Fetch search results from TMDB API
  const handleSearch = useCallback(() => {
    if (!query.trim()) {
      alert('Please enter a cast name to search.');
      return;
    }

    axios
      .get(`${TMDB_BASE_URL}/search/person?query=${query}&include_adult=false&language=en-US&page=1`, {
        headers: {
          Authorization: TMDB_AUTHORIZATION_TOKEN,
        },
      })
      .then((response) => {
        setSearchedPersonList(response.data.results || []);
      })
      .catch((error) => {
        console.error('Error fetching TMDB data:', error);
        setError('Failed to fetch cast data. Please try again.');
      });
  }, [query]);

  // Select a person from the search results
  const handleSelectPerson = (person) => {
    setSelectedPerson(person);
  };

  // Add selected person to the cast list
  const handleAddCast = () => {
    if (!selectedPerson) {
      alert('Please select a person to add as cast.');
      return;
    }

    // Check for duplicates
    const alreadyAdded = addedCast.some((cast) => cast.id === selectedPerson.id);
    if (alreadyAdded) {
      alert('This person is already added to the cast.');
      return;
    }

    setAddedCast((prevCast) => [
      ...prevCast,
      {
        id: selectedPerson.id,
        name: selectedPerson.name,
        url: selectedPerson.profile_path
          ? `https://image.tmdb.org/t/p/original${selectedPerson.profile_path}`
          : '',
        characterName: '', // Optional, can be updated later
        dateCreated: new Date().toISOString(),
        dateUpdated: new Date().toISOString(),
      },
    ]);
    setSelectedPerson(undefined); // Clear selected person
  };

  // Save the cast list to the backend (Create Cast)
  const handleSaveCast = async () => {
    if (addedCast.length === 0) {
      alert('No cast members added.');
      return;
    }

    try {
      for (const cast of addedCast) {
        await axios.post(
          BACKEND_API_URL,
          {
            userId: 1, // Assuming the user ID is 1 for this example
            movieId: movieId,
            name: cast.name,
            characterName: cast.characterName,
            url: cast.url,
          },
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
              'Content-Type': 'application/json',
            },
          }
        );
      }
      alert('Cast successfully saved to the database!');
    } catch (error) {
      console.error('Error saving cast:', error.response?.data || error.message);
      alert('Failed to save cast. Please try again.');
    }
  };

  // Fetch existing cast for the movie (GET by ID)
  const fetchExistingCast = async () => {
    try {
      const response = await axios.get(`http://localhost/movieproject-api/casts/${movieId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      setAddedCast(response.data || []);
    } catch (error) {
      console.error('Error fetching existing cast:', error);
      setError('Failed to fetch existing cast data.');
    }
  };

  // Delete a cast member (DELETE by ID)
  const handleDeleteCast = async (castId) => {
    try {
      await axios.delete(`http://localhost/movieproject-api/admin/casts/${castId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      setAddedCast((prevCast) => prevCast.filter((cast) => cast.id !== castId));
      alert('Cast member deleted successfully!');
    } catch (error) {
      console.error('Error deleting cast:', error);
      alert('Failed to delete cast member.');
    }
  };

  // Load existing cast when component mounts
  useEffect(() => {
    fetchExistingCast();
  }, [movieId]);

  return (
    <div className="cast-and-crew-container">
      <h1>Cast & Crew Management</h1>

      {/* Search Section */}
      <div className="search-container">
        <label>Search Person:</label>
        <input
          type="text"
          value={query}
          onChange={(event) => setQuery(event.target.value)}
          placeholder="Enter cast name"
        />
        <button type="button" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Display TMDB Search Results */}
      {searchedPersonList.length > 0 && (
        <div className="search-results">
          <h3>Search Results:</h3>
          <ul>
            {searchedPersonList.map((person) => (
              <li key={person.id} onClick={() => handleSelectPerson(person)}>
                <img
                  src={`https://image.tmdb.org/t/p/w200${person.profile_path}`}
                  alt={person.name}
                  width={50}
                />
                {person.name}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Display Selected Person */}
      {selectedPerson && (
        <div className="selected-person">
          <h3>Selected Person:</h3>
          <img
            src={`https://image.tmdb.org/t/p/original/${selectedPerson.profile_path}`}
            alt={selectedPerson.name}
            width={100}
          />
          <p>{selectedPerson.name}</p>
          <button type="button" onClick={handleAddCast}>
            Add to Cast
          </button>
        </div>
      )}

      {/* Display Added Cast Members */}
      <div className="added-cast">
        <h3>Added Cast Members:</h3>
        <ul>
          {addedCast.map((cast) => (
            <li key={cast.id}>
              <img src={cast.url} alt={cast.name} width={50} />
              {cast.name} ({cast.characterName || 'No character name'})
              <button onClick={() => handleDeleteCast(cast.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Save Cast to Backend */}
      {addedCast.length > 0 && (
        <button type="button" onClick={handleSaveCast}>
          Save Cast
        </button>
      )}

      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default Cast;
