import React, { useState, useEffect, useContext, useCallback, useRef } from 'react';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import axios from 'axios';
import './CastandCrew.css';
import { useParams } from 'react-router-dom';

function Casts() {
  const { auth } = useContext(AuthContext);
  const [query, setQuery] = useState('');
  const [cast, setCast] = useState([]);
  const [selectedCast, setSelectedCast] = useState({});
  const searchRef = useRef();
  const [notFound, setNotFound] = useState(false);
  const nameRef = useRef();
  const characterNameRef = useRef();
  const urlRef = useRef();
  const { movieId } = useParams();

  // Fetch all casts for a given movie
  const getAll = useCallback((movie_id) => {
    axios({
      method: 'get',
      url: `/movies/${movie_id}`,
      headers: {
        Accept: 'application/json',
        Authorization: `Bearer ${auth.accessToken}`,
      },
    })
      .then((response) => {
        setCast(response.data.casts);
      })
      .catch((error) => {
        console.error("Error fetching Casts:", error.response?.data || error.message);
      });
  }, [auth.accessToken]);

  useEffect(() => {
    getAll(movieId);
  }, [movieId, getAll]);

  // Search for a cast person using external API (TMDB)
  const handleSearchPerson = useCallback(async (page = 1) => {
    setNotFound(true);
    if (!query.trim()) {
      searchRef.current.style.border = '2px solid red';
      setTimeout(() => {
        searchRef.current.style.border = '1px solid #ccc';
        setNotFound(false);
      }, 2000);
      return;
    }
    try {
      const response = await axios({
        method: 'get',
        url: `https://api.themoviedb.org/3/search/person?query=${query}&include_adult=false&language=en-US&page=${page}`,
        headers: {
          accept: 'application/json',
          Authorization: `Bearer ${auth.accessToken}`, // Replace with your actual TMDB API key
        },
      });
      if (response.data.results.length === 0) {
        setNotFound(false);
        setSelectedCast({});
      } else {
        setNotFound(false);
        setSelectedCast(response.data.results[0]);
      }
    } catch (error) {
      console.error("Search error:", error);
    }
  }, [query]);

  // Save new cast details
  const handleSave = async () => {
    if (!characterNameRef.current.value.trim()) {
      characterNameRef.current.style.border = '2px solid red';
      setTimeout(() => {
        characterNameRef.current.style.border = '1px solid #ccc';
      }, 2000);
      return;
    }
    try {
      const dataCast = {
        userId: auth.user.userId,
        movieId: movieId,
        name: selectedCast.name,
        url: `https://image.tmdb.org/t/p/original/${selectedCast.profile_path}`,
        characterName: selectedCast.characterName,
      };
      await axios({
        method: 'POST',
        url: '/admin/casts',
        data: dataCast,
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });
      alert('Added Successfully');
      setSelectedCast({});
      handleClearInput();
      getAll(movieId);
    } catch (error) {
      alert("Error: Unable to save cast. Data might be incomplete.");
      console.error(error);
    }
  };

  // Clear the input fields
  const handleClearInput = () => {
    setQuery("");
    setSelectedCast({});
  };

  // Delete a cast member
  const handleDelete = async (id) => {
    const isConfirm = window.confirm("Are you sure you want to delete this cast?");
    if (isConfirm) {
      try {
        await axios({
          method: 'delete',
          url: `/admin/casts/${id}`,
          headers: {
            Authorization: `Bearer ${auth.accessToken}`,
          },
        });
        getAll(movieId);
        alert("Deleted Successfully!");
      } catch (error) {
        console.error("Delete error:", error);
      }
    }
  };
  // Update selected cast details
  const handleUpdate = async () => {
    if (!selectedCast.id) {
      alert("No cast selected to update.");
      return;
    }

    // Confirm the update action
    const isConfirm = window.confirm("Are you sure you want to update this cast?");
    if (!isConfirm) return;

    // Ensure all required fields are filled in
    if (!selectedCast.name || !selectedCast.characterName || !selectedCast.url) {
      alert("Please fill in all required fields.");
      return;
    }

    // Prepare updated data
    const updatedCast = {
      userId: auth.user.userId, // Assuming userId is part of the selectedCast
      name: selectedCast.name,
      url: selectedCast.url,
      characterName: selectedCast.characterName,
    };

    try {
      // Update the cast member
      const response = await axios({
        method: 'PATCH',
        url: `/admin/casts/${selectedCast.id}`, // Ensure this URL is correct for your API
        data: updatedCast,
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });

      // Handle successful update
      alert('Cast updated successfully!');
      console.log("Update response:", response.data);
      handleClear();  // Clear the selected cast and other state
      getAll(movieId); // Re-fetch the cast list

    } catch (error) {
      // Handle errors
      console.error("Error updating cast:", error);
      if (error.response) {
        console.error("Server response data:", error.response.data);
        alert(`Error updating cast: ${error.response.data.message || 'An unknown error occurred'}`);
      } else {
        console.error("Error request data:", error.request);
        alert("Network error: Please try again later.");
      }
    }
  };

  // Clear selected cast data
  const handleClear = () => {
    setSelectedCast({});
  };

  return (
    <div className="cast-box">
      <div className="Cast-View-Box">
        {cast.length > 0 ? (
          <div className="card-display-cast">
            {cast.map((actor) => (
              <div key={actor.id} className="card">
                <div className="buttons-group">
                  <button
                    type="button"
                    className="delete-button"
                    onClick={() => handleDelete(actor.id)}
                  >
                    Delete
                  </button>
                  <button
                    type="button"
                    className="edit-button"
                    onClick={() => setSelectedCast(actor)}
                  >
                    Edit
                  </button>
                </div>
                <img src={actor.url} alt={actor.name} className="image-casts" />
                <div className="container">
                  <h4>{actor.name}</h4>
                  <p>{actor.characterName}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-cast">
            <h3>Cast not found</h3>
          </div>
        )}
      </div>

      <div className="Search-Box">
        <div className="parent-container">
          <div className="search-box-btn">
            <input
              className="input-search-person"
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search cast name"
              ref={searchRef}
            />
            <button
              className="button-search"
              type="button"
              onClick={() => handleSearchPerson(1)}
              disabled={notFound}
            >
              {notFound ? 'Searching...' : 'Search'}
            </button>
            <button
              className="save-button"
              type="button"
              onClick={handleSave}
              disabled={!selectedCast}
            >
              Add Cast
            </button>
          </div>

          <div className="cast-detail-box">
            <div className="image-container-center">
              <img
                alt="cast"
                src={selectedCast.profile_path ? `https://image.tmdb.org/t/p/original/${selectedCast.profile_path}` : selectedCast.url}
                className="img-cast"
              />
            </div>
            <div className="info-text">
              <div className="input-group">
                <label>Cast Name:</label>
                <input
                  className="cast-name"
                  value={selectedCast.name || ''}
                  onChange={(e) => setSelectedCast({ ...selectedCast, name: e.target.value })}
                  ref={nameRef} />
                  </div>
                  <div className="input-group">
                    <label>Character Name:</label>
                    <input
                      className="character-name"
                      value={selectedCast.characterName || ''}
                      onChange={(e) => setSelectedCast({ ...selectedCast, characterName: e.target.value })}
                      ref={characterNameRef}
                    />
                  </div>
                  <div className="input-group">
                    <label>URL:</label>
                    <input
                      className="url-text-photo"
                      value={selectedCast.url || ''}
                      onChange={(e) => setSelectedCast({ ...selectedCast, url: e.target.value })}
                      ref={urlRef}
                    />
                  </div>
                </div>
                {selectedCast.id && (
                  <div className="edit-back-btn">
                    <button className="edit-btn" type="button" onClick={handleUpdate}>
                      Update
                    </button>
                    <button className="back-btn" type="button" onClick={handleClear}>
                      Back
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      );
    }
    
    export default Casts;
