import axios from 'axios';
import { useState, useEffect, useCallback, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../../../../utils/context/AuthToken';
import './CastandCrew.css';

const CastAndCrew = () => {
  const { auth } = useContext(AuthContext); // Get the access token from AuthContext
  const [query, setQuery] = useState('');
  const [searchedPersonList, setSearchedPersonList] = useState([]);
  const [selectedPerson, setSelectedPerson] = useState(undefined);
  const [addedCast, setAddedCast] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const { movieId } = useParams(); // Get the movieId from URL params

  const API_BASE_URL = 'https://api.themoviedb.org/3';

  // Fetch cast data for the movie using movieId
  useEffect(() => {
    const fetchCast = async () => {
      try {
        const response = await axios.get(
          `${API_BASE_URL}/movie/${movieId}/credits`,
          {
            headers: {
              Authorization: `Bearer ${auth.accessToken}`, // Use the token from AuthContext
            },
          }
        );
        setAddedCast(response.data.cast || []);
      } catch (error) {
        console.error('Error fetching cast:', error);
      }
    };

    if (auth.accessToken && movieId) {
      fetchCast();
    }
  }, [movieId, auth.accessToken]); // Only re-fetch if movieId or token changes

  // Handle the search for a person (cast or crew)
  const handleSearch = useCallback(async () => {
    setNotFound(false);
    try {
      if (!query || query.trim() === '') {
        alert('Please enter a search term.');
        return;
      }

      const response = await axios.get(
        `${API_BASE_URL}/search/person?query=${query}&include_adult=false&language=en-US&page=1`,
        {
          headers: {
            Authorization: `Bearer ${auth.accessToken}`,
          },
        }
      );

      if (response.data.results.length === 0) {
        setNotFound(true);
        setSearchedPersonList([]);
      } else {
        setSearchedPersonList(response.data.results);
      }
    } catch (error) {
      console.error('Error searching for person:', error);
    }
  }, [query, auth.accessToken]);

  // Select a person from search results
  const handleSelectPerson = (person) => {
    setSelectedPerson(person);
  };

  // Add the selected person to the cast list
  const handleAddCast = () => {
    if (!selectedPerson) {
      alert('Please select a person to add to the cast.');
      return;
    }

    // Avoid duplicates
    const personExists = addedCast.some((cast) => cast.id === selectedPerson.id);
    if (personExists) {
      alert('This person is already added to the cast.');
      return;
    }

    setAddedCast((prevCast) => [
      ...prevCast,
      {
        id: selectedPerson.id,
        name: selectedPerson.name,
        profile_path: selectedPerson.profile_path,
        characterName: '', // Placeholder for character name
        dateCreated: new Date().toISOString(),
        dateUpdated: new Date().toISOString(),
      },
    ]);
    setSelectedPerson(undefined); // Reset the selected person after adding
  };

  // Save the added cast to the backend
  const handleSaveCast = async () => {
    if (addedCast.length === 0) {
      alert('No cast members added.');
      return;
    }

    const castData = addedCast.map((person) => ({
      movieId,
      userId: auth.user.userId, // Assuming userId is stored in auth context
      name: person.name,
      profile_path: person.profile_path,
      characterName: person.characterName || '',
      dateCreated: person.dateCreated,
      dateUpdated: person.dateUpdated,
    }));

    try {
      const response = await axios.post('/admin/casts', { cast: castData }, {
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
          'Content-Type': 'application/json',
        },
      });
      alert('Cast successfully added!');
      setAddedCast([]); // Clear the cast list after saving
    } catch (error) {
      console.error('Error adding cast:', error);
      alert('Error adding cast.');
    }
  };

  return (
    <div className='cast-and-crew-container'>
      <h1>Cast & Crew</h1>

      {/* Search for a person */}
      <div className='search-container'>
        Search Person:
        <input
          type='text'
          value={query}
          onChange={(event) => setQuery(event.target.value)}
        />
        <button type='button' onClick={handleSearch}>
          Search
        </button>
        <div className='searched-person'>
          {searchedPersonList.map((person) => (
            <p key={person.id} onClick={() => handleSelectPerson(person)}>
              {person.name}
            </p>
          ))}
        </div>
        {notFound && <p>No results found</p>}
      </div>

      <hr />

      {/* Display selected person */}
      {selectedPerson && (
        <div className='person-details'>
          <img
            className='person-image'
            src={`https://image.tmdb.org/t/p/original/${selectedPerson.profile_path}`}
            alt={selectedPerson.name}
          />
          <div className='person-info'>
            <h3>{selectedPerson.name}</h3>
            <p>Known for: {selectedPerson.known_for_department}</p>
            <p>Biography: {selectedPerson.biography || 'No biography available.'}</p>
          </div>
          <button type='button' onClick={handleAddCast}>
            Add to Cast
          </button>
        </div>
      )}

      {/* Display added cast members */}
      <div className='added-cast'>
        <h2>Added Cast:</h2>
        <ul>
          {addedCast.map((cast) => (
            <li key={cast.id}>
              <img
                className='cast-image'
                src={`https://image.tmdb.org/t/p/original/${cast.profile_path}`}
                alt={cast.name}
                width={50}
              />
              {cast.name} ({cast.characterName || 'Character name not provided'})
            </li>
          ))}
        </ul>
      </div>

      {/* Save Cast */}
      {addedCast.length > 0 && (
        <button type='button' onClick={handleSaveCast}>
          Save Cast
        </button>
      )}
    </div>
  );
};

export default CastAndCrew;
