// Cast.jsx
import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../../utils/context/AuthToken'; // Ensure correct path

const Cast = ({ movieId }) => {
  const context = useContext(AuthContext); // Access auth context
  
  // Check if context is available
  if (!context || !context.auth) {
    return <p>Error: Authentication context is not available.</p>;
  }

  const { auth } = context; // Destructure auth from context

  // Initialize state hooks unconditionally
  const [searchQuery, setSearchQuery] = useState(''); // State for search query
  const [searchResults, setSearchResults] = useState([]); // State for search results
  const [selectedCast, setSelectedCast] = useState([]); // State for selected cast members
  const [isLoading, setIsLoading] = useState(false); // State for loading status
  const [error, setError] = useState(null); // State for error messages

  const API_BASE_URL = 'https://api.themoviedb.org/3';
  const TMDB_HEADERS = {
    Accept: 'application/json',
    Authorization: `Bearer ${auth.accessToken}`, // Use access token from context
  };

  // Handle search for cast member
  const handleSearch = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `${API_BASE_URL}/search/person?query=${searchQuery}&language=en-US`,
        { headers: TMDB_HEADERS }
      );
      setSearchResults(response.data.results);
    } catch (err) {
      setError('Error fetching cast members. Please try again later.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  // Add selected cast to the added list
  const handleAddCast = (cast) => {
    if (!selectedCast.find((member) => member.id === cast.id)) {
      setSelectedCast([...selectedCast, cast]);
    }
  };

  // Save selected cast members to the database
  const handleSaveCast = async () => {
    if (!auth.accessToken) {
      alert('You must be logged in to save cast members.');
      return;
    }

    if (!selectedCast.length) {
      alert('Please add cast members before saving.');
      return;
    }

    try {
      // Save each selected cast member
      for (const cast of selectedCast) {
        const data = {
          userId: auth.user?.userId || 1, // Use logged-in user's ID or default to 1
          movieId,
          name: cast.name,
          characterName: cast.character_name || 'N/A',
          url: `https://image.tmdb.org/t/p/original/${cast.profile_path}`,
        };

        await axios.post(
          'http://localhost/movieproject-api/admin/casts',
          data,
          {
            headers: {
              Authorization: `Bearer ${auth.accessToken}`, // Use Bearer token for auth
            },
          }
        );
      }

      alert('Cast members saved successfully!');
      setSelectedCast([]); // Clear selected cast after saving
    } catch (err) {
      setError('Error saving cast members. Please try again later.');
      console.error(err);
    }
  };

  return (
    <div className="cast-container">
      <h2>Cast Management</h2>

      {error && <p className="text-danger">{error}</p>}

      <div className="search-cast">
        <label>Search Cast:</label>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Enter cast name"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {isLoading && <p>Loading...</p>}

      <div className="search-results">
        {searchResults.length > 0 && (
          <ul>
            {searchResults.map((cast) => (
              <li key={cast.id} onClick={() => handleAddCast(cast)}>
                <img
                  src={`https://image.tmdb.org/t/p/w500${cast.profile_path}`}
                  alt={cast.name}
                  style={{ width: '50px', height: '75px', marginRight: '10px' }}
                />
                <span>{cast.name}</span>
              </li>
            ))}
          </ul>
        )}
        {searchResults.length === 0 && !isLoading && <p>No cast found.</p>}
      </div>

      <div className="selected-cast">
        <h3>Selected Cast Members</h3>
        {selectedCast.length > 0 ? (
          <ul>
            {selectedCast.map((cast) => (
              <li key={cast.id}>
                <img
                  src={`https://image.tmdb.org/t/p/w500${cast.profile_path}`}
                  alt={cast.name}
                  style={{ width: '50px', height: '75px', marginRight: '10px' }}
                />
                <span>{cast.name}</span>
                <button
                  onClick={() =>
                    setSelectedCast(selectedCast.filter((member) => member.id !== cast.id))
                  }
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No cast members added.</p>
        )}
      </div>

      <button onClick={handleSaveCast} className="save-button">
        Save Cast Members
      </button>
    </div>
  );
};

export default Cast;
