import axios from 'axios';
import { useState, useCallback, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../../../../../utils/context/AuthToken'; // Adjusted import path
import './CastandCrew.css';

const CastAndCrew = () => {
  const { auth } = useContext(AuthContext); // Using AuthContext to get the access token
  const [query, setQuery] = useState('');
  const [searchedPersonList, setSearchedPersonList] = useState([]);
  const [selectedPerson, setSelectedPerson] = useState(undefined);
  const [addedCast, setAddedCast] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const { movieId } = useParams();

  // Fetch cast data for the movie when the component mounts or movieId changes
  useEffect(() => {
    const fetchCast = async () => {
      try {
        console.log('Fetching cast data for movieId:', movieId); // Debugging log for movieId
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${movieId}/credits`,
          {
            headers: {
              Authorization: `Bearer ${auth.accessToken}`, // Authorization header with token
            },
          }
        );
        setAddedCast(response.data.cast || []);
      } catch (error) {
        console.error('Error fetching cast:', error.response?.data || error.message);
      }
    };

    if (auth.accessToken) {
      fetchCast();
    } else {
      console.error('Authorization token is missing');
    }
  }, [movieId, auth.accessToken]);

  // Search for people using the TMDB API
  const handleSearch = useCallback(async () => {
    setNotFound(true);
    try {
      if (!query || query.trim() === '') {
        alert('Please enter a search term.');
        return;
      }

      console.log('Searching for:', query); // Debugging log for search query

      const response = await axios({
        method: 'get',
        url: `https://api.themoviedb.org/3/search/person?query=${query}&include_adult=false&language=en-US&page=1`,
        headers: {
          accept: 'application/json',
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });

      if (response.data.results.length === 0) {
        setNotFound(true);
        setSearchedPersonList([]);
      } else {
        setNotFound(false);
        setSearchedPersonList(response.data.results);
      }
    } catch (error) {
      console.error('Error searching for person:', error.response?.data || error.message);
    }
  }, [query, auth.accessToken]);

  // Select a person from the search results
  const handleSelectPerson = (person) => {
    setSelectedPerson(person);
  };

  // Add selected person to the cast list
  const handleAddCast = () => {
    if (!selectedPerson) {
      alert('Please select a person to add as cast.');
      return;
    }

    // Avoid duplicates
    const personExists = addedCast.some((cast) => cast.id === selectedPerson.id);
    if (personExists) {
      alert('This person is already added to the cast.');
      return;
    }

    setAddedCast((prevCast) => [
      ...prevCast,
      {
        id: selectedPerson.id,
        name: selectedPerson.name,
        url: selectedPerson.profile_path
          ? `https://image.tmdb.org/t/p/original/${selectedPerson.profile_path}`
          : '', // Use default image if profile_path is not available
        characterName: '', // Character name can be added later
        dateCreated: new Date().toISOString(),
        dateUpdated: new Date().toISOString(),
      },
    ]);
    setSelectedPerson(undefined); // Reset selected person
  };

  // Save the cast list to the backend
  const handleSaveCast = async () => {
    if (addedCast.length === 0) {
      alert('No cast members added.');
      return;
    }

    const castData = addedCast.map((person) => ({
      movieId,
      userId: auth.user.userId,
      name: person.name,
      url: person.url,
      characterName: person.characterName || '',
      dateCreated: person.dateCreated,
      dateUpdated: person.dateUpdated,
    }));

    try {
      console.log('Saving cast data:', castData); // Debugging log for cast data
      const response = await axios({
        method: 'post',
        url: '/admin/casts', // Adjust this URL to your backend
        data: { cast: castData },
        headers: {
          Authorization: `Bearer ${auth.accessToken}`,
          'Content-Type': 'application/json',
        },
      });
      alert('Cast successfully added!');
      setAddedCast([]); // Clear the cast list after saving
    } catch (error) {
      console.error('Error adding cast:', error.response?.data || error.message);
      alert('Error adding cast.');
    }
  };

  return (
    <div className='cast-and-crew-container'>
      <h1>Cast & Crew</h1>

      {/* Search for a person */}
      <div className='search-container'>
        Search Person:
        <input
          type='text'
          value={query}
          onChange={(event) => setQuery(event.target.value)}
        />
        <button type='button' onClick={handleSearch}>
          Search
        </button>
        <div className='searched-person'>
          {searchedPersonList.map((person) => (
            <p key={person.id} onClick={() => handleSelectPerson(person)}>
              {person.name}
            </p>
          ))}
        </div>
        {notFound && <p>No results found</p>}
      </div>
      <hr />

      {/* Display selected person */}
      {selectedPerson && (
        <div className='person-details'>
          <img
            className='person-image'
            src={`https://image.tmdb.org/t/p/original/${selectedPerson.profile_path}`}
            alt={selectedPerson.name}
          />
          <div className='person-info'>
            <h3>{selectedPerson.name}</h3>
            <p>Known for: {selectedPerson.known_for_department}</p>
            <p>Biography: {selectedPerson.biography || 'No biography available.'}</p>
          </div>
          <button type='button' onClick={handleAddCast}>
            Add to Cast
          </button>
        </div>
      )}

      {/* Display added cast members */}
      <div className='added-cast'>
        <h2>Added Cast:</h2>
        <ul>
          {addedCast.map((cast) => (
            <li key={cast.id}>
              <img
                className='cast-image'
                src={cast.url}
                alt={cast.name}
                width={50}
              />
              {cast.name} ({cast.characterName || 'Character name not provided'})
            </li>
          ))}
        </ul>
      </div>

      {/* Save Cast */}
      {addedCast.length > 0 && (
        <button type='button' onClick={handleSaveCast}>
          Save Cast
        </button>
      )}
    </div>
  );
};

export default CastAndCrew;
