import React, { useContext, useEffect, useRef, useState } from 'react';
import './Videos.css';
import { useNavigate, useParams } from 'react-router-dom';
import { useDebounce } from '../../../../../utils/hooks/useDebounce';
import { AuthContext } from '../../../../../utils/context/AuthToken';
import axios from 'axios';

function Form({ data, state, setState }) {
  const { auth } = useContext(AuthContext);
  const { tmdbId, movieId } = useParams();

  const [selectedData, setSelectedData] = useState(data);
  const [file, setFile] = useState(null); // Use state to manage the file
  const urlRef = useRef();
  const descriptionRef = useRef();

  const [status, setStatus] = useState('idle');
  const navigate = useNavigate();
  const userInputDebounce = useDebounce({ selectedData }, 2000);
  const [debounceState, setDebounceState] = useState(false);
  const [isFieldsDirty, setIsFieldsDirty] = useState(false);

  // alert box state
  const [alertMessage, setAlertMessage] = useState('');
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    setSelectedData(data);
  }, [data]);

  // Handle file input change and set the file in state
  const handleFileChange = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);  // Update the file state
    console.log(uploadedFile);  // This will log the file when it changes
  };

  const handleOnChange = (e) => {
    setDebounceState(false);
    setIsFieldsDirty(true);
    const { name, value } = e.target;

    setSelectedData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Debounce effect
  useEffect(() => {
    setDebounceState(true);
  }, [userInputDebounce]);

  function convertToEmbedUrl(youtubeUrl) {
    const videoIdMatch = youtubeUrl.match(/v=([^&]+)/);

    if (videoIdMatch && videoIdMatch[1]) {
      const videoId = videoIdMatch[1];
      return `https://www.youtube.com/embed/${videoId}`;
    } else {
      // Return the original URL if it doesn't match the pattern
      return youtubeUrl;
    }
  }

  const handleSave = async (event) => {
    setStatus('loading');

    const formData = new FormData();
    formData.append('userId', selectedData.userId);
    formData.append('movieId', tmdbId);
    formData.append('description', selectedData.description);
    formData.append('url', convertToEmbedUrl(selectedData.url));

    if (file) {
      formData.append('video', file); // Only append if a file is selected
    }

    try {
      const response = await axios.post('/videos', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });

      console.log(response.data);
      setIsError(false);
      setAlertMessage(response.data?.message);
      setTimeout(() => {
        setAlertMessage('');
        setState('base');
      }, 2000);
    } catch (error) {
      console.error(error.response);
      setIsError(true);
      setAlertMessage(error.response?.data?.errors[0] || error.response?.data);
      setTimeout(() => setAlertMessage(''), 2000);
    }
  };

  const handleUpdate = async (event) => {
    setStatus('loading');

    const formData = new FormData();
    formData.append('id', selectedData.id);
    formData.append('userId', selectedData.userId);
    formData.append('movieId', tmdbId);
    formData.append('description', selectedData.description);
    formData.append('url', selectedData.url);

    if (file) {
      formData.append('video', file); // Append the file if it exists
    }

    try {
      const response = await axios.patch(`/videos/${selectedData.id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${auth.accessToken}`,
        },
      });

      console.log(response.data);
      setIsError(false);
      setAlertMessage(response.data.message);
      setTimeout(() => {
        setAlertMessage('');
        setState('base');
      }, 2000);
    } catch (error) {
      console.error(error.response);
      setIsError(true);
      setAlertMessage(error.response?.data?.message);
      setTimeout(() => setAlertMessage(''), 2000);
    }
  };

  const handleDelete = () => {
    setStatus('loading');
    const isConfirmed = window.confirm('Are you sure you want to delete this Video?');
    if (isConfirmed) {
      axios
        .delete(`/videos/${selectedData.id}`, {
          headers: {
            Authorization: `Bearer ${auth.accessToken}`,
          },
        })
        .then((response) => {
          console.log('Database Updated');
          console.log(response.data);
          setIsError(false);
          setAlertMessage(response.data.message);
          setTimeout(() => {
            setAlertMessage('');
            setState('base');
          }, 2000);
        })
        .catch((error) => {
          console.error(error.data);
          setIsError(true);
          setAlertMessage(error.data.message);
          setTimeout(() => setAlertMessage(''), 2000);
        });
    }
  };

  return (
    <>
      {alertMessage && <div className={`alert-box ${isError ? 'error' : 'success'}`}>{alertMessage}</div>}
      <div className="video-cards">
        <form>
          <div className="videos-details">
            <div className="field">
              Video URL
              <input
                type="text"
                name="url"
                value={selectedData.url}
                ref={urlRef}
                onChange={handleOnChange}
              />
              {debounceState && isFieldsDirty && !selectedData.url && (
                <span className="errors">This field is required</span>
              )}
            </div>
            <div className="field">
              Description
              <textarea
                rows={5}
                name="description"
                value={selectedData.description}
                ref={descriptionRef}
                onChange={handleOnChange}
              />
              {debounceState && isFieldsDirty && !selectedData.description && (
                <span className="errors">This field is required</span>
              )}
            </div>
          </div>
          <div className="video-profile">
            <iframe
              src={
                selectedData.url && selectedData.url.trim() !== ''
                  ? convertToEmbedUrl(selectedData.url)
                  : 'https://via.placeholder.com/550x300?text=No+Video'
              }
              className="video-frame"
              allowFullScreen
            />
          </div>
        </form>
      </div>
      <div className="button-group">
        {state === 'update' && (
          <button onClick={handleDelete} className="delete-button">
            Delete
          </button>
        )}
        <button
          onClick={() => {
            if (status === 'loading') {
              return;
            }
            const { url, description } = selectedData;
            if (url && description) {
              state === 'update' ? handleUpdate() : handleSave();
            } else {
              // Fields are incomplete
              setIsFieldsDirty(true);
              // Focus if field is empty
              if (!url) {
                urlRef.current.focus();
              } else if (!description) {
                descriptionRef.current.focus();
              }
            }
          }}
        >
          Save
        </button>
      </div>
    </>
  );
}

export default Form;
